var app = {

    // Application Constructor
    initialize: function() {
        this.generateBook()
        this.generateFavs()

        $(".livre").on('click', this.openBook)

    },

    //recuperer le localStorage deja existant si vide :
    getDataBook: function() {
        const books = localStorage.getItem("books");
        if (books == null)
            return []
        else
            return JSON.parse(books)
    },
    getDataFav: function() {
        const favs = localStorage.getItem("favoris");
        if (favs == null)
            return []
        else
            return favs
    },
    //cree les livres sur l'index.html
    generateBook: function() {
        let books = app.getDataBook();
        $('#Bibliotheque').html('')
        for (let i = 0; i < books.length; i++)
            $('#Bibliotheque').append(` 
                <div class="livre book col-4" data-index="${i}" >
                    <div class="handle" data-index="${i}" data-favoris="false"></div>
                    <p class="titre m-0 text-center text-white" id="titre-${i}">${books[i][0].Title}</p>
                </div>
            `)
    },
    generateFavs: function() {
        let books = app.getDataFav();

        if (JSON.stringify(books) != "[]") {

            $('#Favoris').html('')
            $('#Favoris').append(` 
                <h4 class="my-1 text-white container col-10" id="favo"> Favoris</h4>
            `)
            for (let i = 0; i < books.length; i++)


                $('#Favoris').append(` 
                <div class="livre book col-4" data-index="${app.getid(books[i][0].Title)}" >
                    <div class="handle" data-index="${app.getid(books[i][0].Title)}" data-favoris="true"></div>
                    <p class="titre m-0 text-center text-white" id="titre-${app.getid(books[i][0].Title)}">${books[i][0].Title}</p>
                </div>
            `)
            $('#Favoris').append(` 
                <h4 class="my-1 text-white container col-10" id="favo">Bibliotheque</h4>
            `)
        }

    },

    openBook: function() {
        index = $(this).data('index')
        books = localStorage.getItem("books");
        books = JSON.parse(books)
        let loaded = books[index]
        localStorage.setItem("loaded", JSON.stringify(loaded));
        window.location.href = "read.html"

    },

    deleteBook: function(index) {
        if (confirm("/!\\ Attention le livre sera supprimé de la Bibliotheque et des favoris /!\\")) {
            let books = localStorage.getItem("books");
            books = JSON.parse(books);
            app.delfav(JSON.parse(localStorage.getItem("favoris")), books[index][0].Title);

            books.splice(index, 1)
            localStorage.setItem("books", JSON.stringify(books));

            document.location.reload(true)
        }
    },
    getid: function(title) {
        books = app.getDataBook()
        id = books.findIndex((item) => {
            return item[0].Title == title
        });
        return id
    },
    addfav: function(favoris, fav) {

        favoris = favoris.filter((item) => {
            return item[0].Title == fav
        });
        favs = app.getDataFav()
        newfav = favs.concat(favoris)
        localStorage.setItem("favoris", JSON.stringify(newfav));


    },

    delfav: function(favoris, fav) {

        favoris = favoris.filter((item) => {
            return item[0].Title != fav
        });

        localStorage.setItem("favoris", JSON.stringify(favoris));

    },

    getDataFav: function() {
        const favs = localStorage.getItem("favoris");
        if (favs == null)
            return []
        else
            return JSON.parse(favs)
    },

    favBook: function(index, check) {

        let favoris = localStorage.getItem("favoris");
        favoris = JSON.parse(favoris)
        let books = localStorage.getItem("books");
        books = JSON.parse(books)
        let favt = books[index][0].Title

        if (check == "true") { // retire des favoris
            app.delfav(favoris, favt)
            document.location.reload(true)

            return "false"
        } else {
            app.addfav(books, favt)
            document.location.reload(true)

            return "true"
        }

    },

    searchBook: function() {
        favoris = document.getElementById("Favoris")
        favoris.style.display = "none";

        var input, filter, value, div, divs;
        input = document.getElementById('searchBar')
        filter = input.value.toUpperCase();
        div = document.getElementById('Bibliotheque');
        divs = div.getElementsByClassName('livre');
        for (var i = 0; i < divs.length; i++) {
            titre = document.getElementById(`titre-${i}`);
            value = titre.textContent || titre.innerText;

            if (value.toUpperCase().indexOf(filter) > -1) {
                divs[i].style.display = "";
            } else {
                divs[i].style.display = "none";
            }
        }
        if (filter == "")
            favoris.style.display = "";
    }
};

$(".fav").on('drop', function() {
    val = app.favBook($(event.target).attr('data-index'), $(event.target).attr("data-favoris"));
    $(event.target).attr("data-favoris", val)
})
$(".del").on('drop', function() {
    app.deleteBook($(event.target).attr('data-index'))
})



data = [
    [{ "Title": "Minecraft" }, { "Chapitres": [{ "number": "1", "text": "Le jeu plonge le joueur dans un monde créé dynamiquement, composé de blocs (des cubes) représentant différents matériaux comme de la terre, du sable, de la pierre, de l'eau , de la lave ou des minerais (comme du fer, de l'or, du charbon, etc.) formant diverses structures (arbres, cavernes, montagnes, temples) et des animaux ou monstres tels que des vaches , des moutons , des zombies et des squelettes . Le joueur peut modifier ce monde à volonté en y ajoutant ou supprimant des blocs et en tentant de survivre le plus longtemps possible lui permettant ainsi de bâtir des constructions avec une grande liberté, rappelant ainsi les jeux de création Lego.\nLe joueur est représenté par un personnage, de forme humanoïde sans sexe prédéfini, du nom du pseudonyme demandé lors de l'achat du jeu. Son apparence peut être personnalisée sur le site officiel, mais il a par défaut une apparence nommée Steve, possédant un jean bleu et une veste turquoise, ou une nommée Alex, d'un jean marron, une veste verte, et aux cheveux roux longs. La vue par défaut du joueur est à la première personne, qu'il peut faire passer en deuxième personne et troisième personne dans les options. Il possède une jauge de vie de vingt points de vie ainsi qu'une jauge de nourriture. Si sa barre de vie atteint 0, le joueur ressuscite à son emplacement de départ sur la carte où à côté du dernier lit dans lequel il a dormi.\nLe joueur, par défaut droitier, peut utiliser ses deux mains, mais seule la main de préférence peut effectuer des actions telles que : combattre, casser ou poser des blocs. Il possède un inventaire de 27 cases, chacune pouvant contenir le plus souvent jusqu'à 64 objets d'un même type (un stack), ainsi qu'une barre d'accès rapide de 9 cases (l'inventaire rapide), et une case pour la seconde main. Dans son inventaire, il peut aussi fabriquer des petits objets grâce à une grille de fabrication de 2 cases sur deux ou équiper une armure.\nBien que le jeu ne se termine jamais à proprement parler, il possède une fin : il s'agit d'un long dialogue entre deux Endermen qui s'affiche quand le joueur a tué l'Ender Dragon, un boss vivant dans le monde de l'End et qui est réservé aux joueurs chevronnés de par sa difficulté et du fait que l'End n'est accessible qu'après de nombreux prérequis. Après cette scène, le joueur réapparait à la surface et peut continuer à jouer. Il est également possible de « terminer » Minecraft en collectant tous les trophées ou en rassemblant un exemplaire de chaque matière ou chaque objet du jeu, par exemple, selon l'imagination et la patience du joueur.\n\nMonde\n\nUn des grands intérêts du jeu réside dans la création dynamique du monde : chaque carte est générée aléatoirement au fur et à mesure que le joueur avance, selon une graine de monde générée selon l'heure de l'ordinateur. Cependant, les paysages sont cohérents et composés de plusieurs biomes (forêt, prairie, désert, jungle, toundra, taïga, marais, savane, etc.). Une carte donnée peut devenir très grande au fil de sa génération, étant quasiment infinie (elle mesure 60 millions de blocs par 60 millions de blocs, soit 8 fois la surface de la Terre). Il est possible de jouer seul dans une carte générée dynamiquement, ou en multijoueur sur un serveur existant ou créé pour l'occasion. Une carte est constituée de 256 couches : même s'il est possible d'aller au-dessus ou en dessous de ces limites, le joueur ne pourra pas poser de blocs. Sous la dernière couche, il n'y a que du vide : tomber dedans fait chuter le joueur jusqu'à ce que mort s'ensuive.\nLe monde principal est composé d'une surface, dépendante de chaque biome, et de souterrains, plus ou moins remplis de pierre, où se génèrent aléatoirement des minerais, des grottes ou des cavernes22. Des structures comme des donjons apparaissent également aléatoirement sur la carte, à la surface ou dans les souterrains.\nUn monde est aussi composé de deux autres dimensions, le Tréfonds (ou Enfer ou Nether) et le Néant (ou Limbes ou End ou Ender). Le Tréfonds est une dimension fermée ressemblant à un enfer, sans nuit ni jour, accessible par un portail que le joueur doit créer. Il est composé essentiellement de lave et peuplé de monstres uniques comme les cochons-zombies ou les ghasts (fantâmes), et est utilisable par les joueurs pour traverser de longues distances sur le monde principal, du fait du rapport 1/8e des distances parcourues dans cette dimension. Le Néant est quant à lui une autre dimension, apparue lors de la sortie officielle du jeu, consistant en une île volante, entourée de vide, où se trouvent des endermen et l'Ender Dragon, à tuer pour débloquer la supposée fin du jeu.\nLe monde contient environ 300 différents types de blocs, la plupart représentant des matériaux de construction ou des éléments de décor. Un bloc fait en général un mètre de côté, et par conséquent 1 m3 de volume. Les blocs peuvent être solides ou non-solides (air, torches, etc.), transparents (verre, glace, etc.) ou opaques, fluides (eau, lave), et nécessitent souvent un outil particulier pour être récupérés (pioche en bois ou plus pour la pierre, mais pioche en diamant pour l'obsidienne...). La gravité n'affecte pas la quasi-majorité des blocs, hormis les fluides et certaines matières (sable, gravier, etc.).\n\nCréatures\n\nDe nombreuses créatures peuplent le monde du jeu. Le joueur peut interagir avec elles pour se défendre ou s'approvisionner en ressources. Il en existe quatre types : les créatures passives qui n'attaquent jamais le joueur (vaches, cochons, poules, etc.), les monstres neutres qui n'attaquent pas à moins qu'on ne les provoque (les cochons-zombies, les loups, les Endermen, etc.), les monstres agressifs qui attaquent le joueur à vue (zombies, Creepers, squelettes, etc.) et les créatures amicales et invoquées comme les golems de fer ou les bonshommes de neige qui accompagnent et aident le joueur. Certains animaux peuvent être apprivoisés grâce à divers objets, tels que l'os pour le loup, le poisson cru pour l'ocelot ou le blé pour le cheval, ce qui leur permet également de se reproduire. Les créatures agressives ainsi que certaines neutres n'apparaissent que dans des zones de faible luminosité (hors difficulté paisible) : grottes, cavernes, ou la nuit.\nCertaines créatures sont devenues emblématiques : les Endermen, de grandes créatures noires pouvant se téléporter, qui n'attaquent que si le joueur les provoque en les regardant dans les yeux ou en les frappant, ou encore les Creepers, véritables symboles du jeu, qui explosent et détruisent plusieurs blocs quand ils sont suffisamment près du joueur. Le joueur pourra trouver d'autres monstres en explorant diverses biomes comme des sorcières dans les marécages.\nMinecraft compte aussi deux « boss » principaux : l'Ender Dragon, dragon établi dans une dimension parallèle nommé l'Ender, et le Wither, monstre volant à trois têtes apparu dans la mise à jour 1.4.2, que le joueur peut créer en plaçant dans une position particulière du « sable des âmes » et des crânes de Wither squelettes, obtenables en tuant ces derniers, présents dans la dimension du Nether.\n\nArmes, armures et outils\n\nPour chaque action, le joueur peut ou doit se munir d'un outil pour l'accomplir, ou accélérer son accomplissement : pioche, hache, pelle, houe, etc. Certains outils peuvent se fabriquer avec différents minéraux ou matériaux, donnant plus ou moins de durabilité et d'efficacité à ceux-ci : respectivement le bois, la pierre, le fer, et le diamant, ainsi que l'or. D'autres ne se fabriquent qu'avec une recette unique, et peuvent effectuer une action impossible sans cette chose (canne à pêche, briquet) ou donner des informations (boussole, horloge).\nLe joueur peut aussi se fabriquer une épée, afin de donner des attaques plus puissantes lors des combats. Tout comme les principaux outils, elle peut être faite de divers matériaux lui donnant plus ou moins de robustesse et de puissance. Il peut s'équiper d'une armure (composée de quatre pièces : casque, plastron, jambières et bottes) pour se défendre et réduire la puissance des attaques ennemies, dont le matériau utilisé fera varier la résistance aux chocs. Il peut enfin se fabriquer un bouclier, qui permet de stopper n'importe quel type d'attaque (explosions, projectiles, coups…) mais qui ne protège pas des dégâts de potion, de chute ou de feu. Ce bouclier n'existe que sous une seule forme mais peut être personnalisé avec une bannière.\nLa plupart des outils, armures et armes, peuvent être enchantés avec une table d'enchantement, contre de l'expérience récoltée par le joueur en faisant diverses actions (miner des minerais, tuer des créatures, etc.). Les enchantements donnés sont aléatoires, mais leur puissance et leur fréquence dépend du nombre d'expériences donné, ainsi que du matériau de l'outil. La plupart des enchantements sont réservés à un type d'outil ou d'arme, mais certains peuvent s'appliquer à tous. Certains enchantements ne peuvent également être trouvés que sous forme de livres dans des structures.\n\nBibliothèque libre\n\nLe jeu inclut une bibliothèque libre, permettant de contourner la censure mise en place par certains pays.\n\nModes de jeu\n\nMode créatif\nLe mode créatif permet au joueur de construire et de détruire les blocs instantanément à volonté. Il n'y a pas de fabrication d'objet : la quasi-majorité des blocs et objets est disponible dans l'inventaire du joueur. Il est aussi possible de voler en pressant deux fois de suite la touche de saut (barre d'espace). Ce mode est disponible en solo et en multijoueur. À l'origine, les cartes étaient plus petites et moins diversifiées que celles du mode survie. Il s'agit du premier mode de jeu gratuit disponible à l'origine. Il a été supprimé du jeu lors de la nouvelle version Survival Test.\nÀ partir de la version 1.8 bêta de Minecraft, ce mode est réintroduit sous forme de mode de jeu appelé mode créatif, en plus du mode Survie. Cependant, dans cette version, les cartes générées en mode créatif sont aussi riches et variées que celles des autres modes de jeu. En mode créatif, les monstres et autres dangers ne blessent pas le joueur, il ne peut mourir, sauf s'il utilise une commande ou tombe dans le vide sous la couche 0 d'une carte.\nCe mode de jeu peut être choisi lors de la création d'un monde, ou par une commande. Il est très prisé par les architectes et ingénieurs car il permet d'accéder aux ressources de manière illimitée sans avoir à les collecter.\n\nMode survie\nLe mode survie plonge le joueur dans un monde peuplé de monstres qui apparaissent dans les endroits sombres, c'est-à-dire la nuit et dans les grottes non éclairées. C'est le mode principal et par défaut des mondes Minecraft.\nLe joueur doit survivre et être capable de se défendre. Pour cela, il peut fabriquer des outils : par exemple, à partir de bois pour concevoir des planches et des bâtons ; avec des bâtons et de la pierre pour fabriquer une pioche ou bien une épée ; avec une pioche, il peut extraire du charbon et du fer. De certaines matières telles que le fer, le diamant ou encore le cuir, le joueur pourra se faire des armures pour résister aux monstres. Chaque outil est construit à partir d'éléments basiques récoltés. Il est également nécessaire de chasser des animaux ou de cultiver du blé, des pommes de terres ou des carottes afin de s'approvisionner en nourriture.\nDernier point remarquable, la redstone permet de concevoir des circuits logiques et de rendre le monde interactif : il est ainsi possible de fabriquer des portes à ouverture automatique, des générateurs de sable ou encore une calculatrice. Le joueur peut également utiliser des circuits de redstone pour automatiser certaines tâches comme l'agriculture ou l'élevage. La redstone n'a presque pas de limite, elle permet même d'accomplir des prouesses informatiques et numériques immenses.\n\nMode extrême\nLe mode extrême (hardcore) est un mode de jeu similaire au mode survie, mais où le joueur n'a qu'une seule vie et la difficulté est bloquée au niveau maximum. Si le joueur meurt, il ne peut pas ressusciter et le monde sera supprimé à tout jamais, il n'a donc pas le droit à l'erreur.\nDans ce mode, l'apparence des cœurs de la barre de vie est différente : ils ont deux yeux leur donnant un air diabolique. À la fin de la partie, le score du joueur s'affiche, correspondant aux nombres de monstres tués, et donc à l'expérience acquise au cours du jeu.\nApparu dans la version définitive du jeu (1.0.0), il est inspiré d'une série d'articles sur le site PC Gamer , où l'auteur s'était fixé comme règle de supprimer son monde à la première mort du personnage, tout en allant le plus loin possible.\n\nMode aventure\nLe mode aventure, apparu dans la version de développement 12w22a (1.3.1), est un mode de jeu similaire au mode survie, mais où il est impossible par défaut de placer ou détruire des blocs. Pour survivre, il doit donc commercer avec les villageois en utilisant des émeraudes comme monnaie d’échange, et prendre ce qu'il y a dans les coffres des donjons, des mines abandonnées et des forges. Entre les versions 1.4.2 et 1.8, le joueur peut utiliser des outils, mais uniquement ceux appropriés dans certaines situations : l’épée contre les monstres, la pioche pour les minerais et la roche, la hache pour le bois, etc. Depuis la version 1.8, les joueurs peuvent détruire les blocs avec l'outil approprié seulement si ce dernier a été modifié avec une commande.\nCe mode de jeu est souvent utilisé pour les cartes aventures. Les cartes aventures sont des parties enregistrées et partagées sur Internet par des passionnés pour les autres joueurs dont les objectifs, le scénario et les constructions dépendent entièrement du choix du ou des créateurs. Le mode aventure sert alors à empêcher les joueurs de jouer au jeu normalement sur les cartes aventure et de limiter leurs modifications sur les blocs à ce qui est prévu, car cela pourrait gêner le bon déroulement de la partie.\n\nMode spectateur\nLe mode spectateur, apparu dans la version de développement 14w05a pour la version finale 1.8, permet au joueur de traverser les blocs et de se rendre invisible (sauf aux yeux des autres joueurs spectateurs, qui se voient entre eux sous la forme d'une tête flottante semi-transparente). Il ne dispose pas d'ATH (HUD) mais la barre d'inventaire peut être affichée, devenant alors un menu séparé en deux sous-parties : l'une affiche tous les joueurs connectés au serveur, et l'autre trie les joueurs selon leur équipe. Le joueur spectateur peut également voir en vue à la première personne à travers les yeux d'un personnage de son choix en faisant clic gauche. Il ne peut pas interagir ou modifier le monde. Ce mode est surtout utilisé sur serveur multijoueur, pour pouvoir surveiller, et visionner une partie ou un monde, sans pouvoir les modifier.", "chapTitle": "Système de jeu" }, { "number": "2", "text": "Le mode multijoueur est la version serveur du jeu qui permet à plusieurs joueurs d'interagir sur un monde unique. Les joueurs peuvent travailler avec d'autres pour créer des structures, des mines et se combattre : le joueur contre joueur (abrégé en JcJ ou PvP pour Player versus Player).\nLes utilisateurs peuvent télécharger et exécuter le pack serveur qui permet aux autres joueurs de se connecter à et jouer sur leurs serveurs. Différents modes de jeu peuvent être choisis pour un serveur, parmi lesquels le mode survie, créatif, hardcore, spectateur et aventure. Il convient de noter que les modes de jeu peuvent être attribués individuellement, certains pouvant être en mode créatif et d'autres en mode survie. Les joueurs peuvent également être promus opérateur (avec la commande /op [nom du joueur] (gestionnaire du réseau) par l'administrateur du serveur ou d'autres opérateurs, qui leur donne accès aux commandes console telles que la configuration de l'heure et du temps, la téléportation de joueurs et l'accès au give (commande permettant de donner des ressources à certains joueurs).\nMais pour la plus grande majorité des serveurs grand public, Bukkit est utilisé. C'est une plate-forme développée par un groupe indépendant, qui permet d'énormes possibilités. Il permet d'installer des plugins, du code informatique en langage Java ajoutant des fonctionnalités au jeu d'origine : de nouvelles commandes, des permissions, une économie, un système dit « RP », pour Role Play, ou encore des plugins permettant de faire apparaître des personnages non-joueurs. Les possibilités sont infinies, puisque les plugins sont également codés en Java, et aucune restriction n'est imposée par Bukkit.\nPour les joueurs ne souhaitant pas rendre leur serveur visible sur internet, il est possible de le lancer sur le réseau local (en utilisant l'adresse IP locale par opposition à une adresse IP globale présentée par le routeur du serveur). Un serveur local n'exige pas la redirection de port, mais devient alors restreint aux seuls joueurs faisant partie du réseau local l'accès du serveur. Cette méthode peut également être utilisée pour améliorer les performances du jeu en solo si le serveur et le client sont deux ordinateurs distincts, en déportant une partie des calculs sur la machine serveur. Les temps de latence ne sont en général pas un problème sur un réseau local.\n\nServeurs\n\nMinecraft comprend une liste de serveurs pour le mode multijoueur dans lequel les utilisateurs peuvent s'enregistrer les serveurs qu'ils visitent. Bien que Minecraft n'ait pas de liste de serveurs pré-remplie directement disponibles à partir du jeu lui-même ou sur le site officiel de Minecraft, des sites de liste de serveur Minecraft existent pour permettre aux joueurs de visiter et de découvrir de nouveaux serveurs. De plus, le lanceur Minecraft autonome supporte l'ajout de serveurs dans la section multijoueur. Cette section permet aux joueurs de voir si un serveur est en ligne, le nombre d'emplacements qu'il a (appelés slots de connexion), et la qualité de la connexion au serveur (temps de ping). Planer au-dessus de l'icône d'état de la connexion dans la liste des serveurs de Minecraft va dire au joueur la vitesse de connexion à ce dernier. La version utilisée par le serveur est également affichée à côté du temps de ping, et depuis la version 1.7, les serveurs ont la possibilité d'afficher leur logo. La liste des serveurs d'un joueur peut être transférée à d'autres joueurs par échange du fichier servers.dat contenu dans le dossier du jeu.\n\nChangement de pseudo\n\nIl est possible de changer de pseudo Minecraft depuis le 4 février 2015. Un joueur ne peut changer de pseudo que tous les 30 jours, mais son ancien pseudo est inaccessible aux autres pendant 37 jours au cas où le joueur change d'avis et veut revenir à son ancien pseudo. Au bout de 37 jours, l'ancien pseudo redevient accessible à tous.", "chapTitle": "Multijoueur" }, { "number": "3", "text": "Le jeu est initialement créé le 10 mai 2009 par Notch, de son vrai nom Markus Persson. Il tire son inspiration de jeux plus anciens comme Infiniminer (pour le système de cubes et de création et récupération de ressources), Dwarf Fortress (dont il tire l'aspect de très grande liberté couplée avec une génération d'univers procédurale et reprend nombre des types de blocs), ou encore Dungeon Keeper (qui présente un monde souterrain que l'on doit excaver judicieusement pour y aménager son propre donjon).\nÀ ses débuts, le jeu s'appele Cave Game, puis le nom définitif de Minecraft est inventé lors d'un échange sur IRC entre le développeur et des joueurs. Après avoir été nommé Minecraft: Order of the Stone (en référence à un webcomic du nom de Order of the Stick), le nom est finalement raccourci en Minecraft pour plus de simplicité.\nLe 17 mai, le jeu est disponible publiquement pour être essayé. Les pré-commandes pour le jeu commencent à être acceptées le 19 juin 2009, pour un prix promotionnel de 9,99 €. Il est à noter que ce prix, valable pour la version Alpha, est porté à 14,99 € pour la version Beta. Minecraft gagne rapidement en popularité, étant la source d'articles sur plusieurs sites internet comme PC Gamer. En avril 2011, le jeu dépasse la barre des deux millions d'exemplaires vendus. Victime de son succès, le site du jeu tombe inaccessible le 20 octobre 2010.\nLe jeu est en version stable mais continue d'être mis à jour. Les joueurs peuvent actuellement acheter le jeu en version définitive, disponible depuis le 18 novembre 2011, pour y jouer et le tester. En achetant la version définitive, les joueurs peuvent essayer la dernière version disponible du jeu et peuvent, sur le forum du jeu, faire part des bugs ou des nouveaux éléments qu'ils aimeraient voir dans le jeu. Les possesseurs d'une version achetée lors de la phase alpha ou inférieure possèdent la garantie de futures mises à jour gratuites à la suite d'un changement de clause de licence.\nLe 7 avril 2011, Notch annonce une date de sortie prévue pour le 18 novembre 2011, le jour de la Minecon à Las Vegas qui met fin à la phase bêta. La version 1.4.2 de Minecraft sort pour Halloween 2012. Le jeu est depuis le 22 octobre 2018 sous la version 1.13.2.\nSur le site officiel, Notch précise que lorsque les ventes auront chuté, il pourrait mettre le jeu en open source, soit sous une licence GPL ou une autre licence libre, soit dans le domaine public, mais cette mention a été retirée depuis.\n\nVersions de développement\n\nAfin de bénéficier de retours de la part de la communauté, les développeurs sortent, entre deux grandes mises à jour, des versions de développement, dites snapshots, contenant les fonctionnalités à venir dans les prochaines versions. Instituées depuis le 9 septembre 2011 avec la snapshot Bêta 1.8-pre1, ces versions sont le plus souvent hebdomadaires, sortant en temps normal à une date fixe, devenant ainsi un rendez-vous pour la communauté. Les joueurs peuvent ainsi signaler les bogues (bug) ou tout autre commentaire permettant d'améliorer le jeu, notamment sur le forum Reddit.\n\nMusiques\n\nLa bande sonore de Minecraft est composée par le musicien indépendant allemand Daniel Rosenfeld, plus communément appelé C418. Elle est disponible dans les albums Minecraft - Volume Alpha, disponible depuis mars 2011, regroupant la plupart des musiques originelles ainsi que d'autres musiques non ajoutées dans le jeu, et Minecraft - Volume Beta, disponible depuis novembre 2013, contenant des musiques ajoutées dans le jeu le 13 novembre 2013.\nParticipation de la communauté\n\nUne grande variété de contenu créé par la communauté, comme des packs de textures, des mondes customisés, des constructions, des logiciels , etc. est disponible gratuitement sur divers sites internets. Des modifications du code source de Minecraft, appelés mods, ajoutent une diversité de gameplay, de blocs, de créatures hostiles et passives, d'objets ou encore de nouveaux systèmes complets (industrie, mécanique, etc.). Pour rendre la création et l'installation de mods plus aisé, Mojang a annoncé en novembre 2012 qu'ils allaient ajouter une API officielle, toujours en attente.\nDes packs de textures permettent de personnaliser les éléments graphiques du jeu. À partir de la version 1.6, les packs de textures ont été remplacés par des packs de ressources. Ceux-ci jouent le même rôle, mais ajoutent la configuration des bruitages et musiques, des polices d'écritures, des crédits et des fichiers de langue. Les mondes personnalisés sont également populaires. Les joueurs peuvent créer leur propres mondes, qui contiennent souvent des défis, des puzzles, des objectifs, et les partager pour que d'autres puissent y jouer. Pour encourager et faciliter cette pratique, les développeurs ont ajouté le « mode aventure » en version 1.3, et les « blocs de commandes » en version 1.4.\nEn plusieurs occasions, le travail de la communauté a influé sur le développement même du jeu. Des éléments développés par des tiers sous forme de mod ont ainsi été intégrés par la suite à la version officielle de Minecraft, comme les pistons ou les chevaux.\nD'autre part, la notoriété du jeu est en majeure partie attribuable à la communauté. L'aspect créatif du jeu a rapidement incité des joueurs de tous horizons à partager leurs créations, notamment par le biais de vidéos sur des sites spécialisés tel YouTube ou Twitch, ce qui a offert une très forte visibilité et popularité au jeu sans aucune intervention de Mojang. L'aspect « jeu de rôle/aventure » du jeu est également à l'origine de machinimas et de fictions, de plus, les graphismes simples du jeu étant faciles à animer, certains amateurs réalisent des dessins animés ou des courts métrages d'animation inspirés du jeu.\nLa première collection de boîtes de jeu Lego, appelée Micro World, est apparue en 2012, d'après une idée soumise à Lego par des fans. Constituées uniquement de petites pièces, et non de figurines complètes, elles représentent divers lieux du jeu. Mojang et Lego renouvelèrent leur partenariat en 2014 pour une nouvelle collection classique, plus proche d'une boîte ordinaire, en impliquant la communauté dans la création des modèles. Ainsi, les joueurs purent proposer leurs idées et donner leur avis aux designers Lego durant la conception des nouvelles boîtes.\n\nRachat de Mojang par Microsoft\nLe 15 septembre 2014, les diverses rumeurs circulant sur Internet sont confirmées : le studio de développement Mojang, créateur de Minecraft, est racheté par Microsoft pour environ 2,5 milliards de dollars (soit un peu moins de 2 milliards d'euros).\nLe fondateur, Markus Persson, déclare qu'il quitte totalement le développement de Minecraft pour préserver sa santé mentale (« It’s not about the money. It’s about my sanity. ») tout comme Carl et Jacob. Il annonce qu'il ne veut ni rejoindre Microsoft ni rester chef d'entreprise. Il ajoute que Minecraft restera disponible sur toutes les plateformes.", "chapTitle": "Développement" }, { "number": "4", "text": "Portages sur console\n\nUne adaptation sur Xbox 360 sort le 9 mai 2012 et est actuellement en version TU19. D'après Notch, interrogé par IGN, « le soft devrait être conçu pour mieux marcher sur console, et devrait s'axer sur une façon de jouer propre aux consoles ».\nUne version Xbox One de Minecraft est également disponible depuis le 5 septembre 2014. La version actuelle est la TU1 (1.3.1).\nLe 20 août 2013, lors de la conférence PlayStation à la Gamescom, les versions PS3, PS4 et PSVita sont dévoilées. La version PS3 du jeu est la première à sortir, le 18 décembre 2013. Elle est similaire à la version Xbox 360. Le jeu est actuellement à la version égale à celle de la 1.8.8 sur PC et Mac.\nUne version PS Vita est annoncée et confirmée le 4 septembre 2014. Elle est disponible depuis le 15 octobre 2014 sur le PlayStation Store. La version physique est disponible depuis le 19 novembre 2014.\nUne version Playstation 4 est disponible depuis le 4 septembre 2014 sur le PlayStation Store9. Il est possible d'importer ses sauvegardes de la version PlayStation 3 en passant par internet.\nUne version Wii U de Minecraft est disponible depuis le 17 décembre 2015 sur le Nintendo eShop. Elle est catégorisée le 11 novembre 2015 par PEGI.\nUne version Nintendo Switch est disponible depuis le 12 mai 2017 sur le Nintendo eShop et en version physique. Initialement baptisée Minecraft Nintendo Switch Edition, le jeu est renommé simplement Minecraft lors d'une mise à jour.\nUne version New Nintendo 3DS est annoncée le 14 septembre 2017.\n\nPocket Edition\n\nMinecraft Pocket Edition est une édition du jeu pour smartphones et tablettes. La version actuelle est la version 1.2.10. Minecraft Pocket Edition est disponible sur les smartphones Android depuis le 15 juillet 2011 au prix de 6,99 $ (6,99 €), sur les terminaux iOS au prix de 9,99 $ (6,99 €) et sur les smartphones Windows Phone au prix de 6,99 $ (6,89 €).\nMinecraft Pocket Édition n'est pas un portage fidèle de la version PC, des adaptations ont en effet été réalisées pour s'adapter aux supports mobiles. Ainsi, certains blocs présents dans les autres éditions ne sont pas présents, alors que d'autres font leur apparition.\nCette version de Minecraft est la plus téléchargée de toutes les éditions, en dépassant à elle seule le total des éditions Xbox (12 millions) et PC (17 millions) pour un total de 30 millions de téléchargements.\n\nRaspberry Pi\n\nDepuis le 20 décembre 2012, une version de Minecraft est disponible pour Raspberry Pi. Cette édition est la seule à être téléchargeable gratuitement, cependant elle ne fut jamais mise à jour.\n\nRéalité augmentée\nLe 15 juin 2015, Microsoft dévoile lors de l’E3 2015 une version de Minecraft compatible avec Microsoft HoloLens, le casque à réalité augmentée. Mojang se livre même à une démonstration en direct sur scène.", "chapTitle": "Autres versions" }, { "number": "5", "text": "Minecraft: Story Mode\n\nUn projet de collaboration entre Mojang et Telltale Games intitulé Minecraft: Story Mode a été annoncé en décembre 2014. Il s'agit d'un jeu vidéo épisodique autonome en point-and-click dont les choix du joueur influencent l'histoire. Le premier épisode, L'Ordre de la Pierre, est sorti sur PC, Mac, iOS, Android, PlayStation 3, PlayStation 4, Xbox 360 et Xbox One le 13 octobre 2015. La première saison est composée de cinq épisodes principaux et trois épisodes supplémentaires. Une seconde saison est proposée depuis juillet 2017.\n\nMinecraft: Education Edition\n\nLe 19 janvier 2016, Mojang annonce le développement d'une nouvelle mouture du jeu, destinée aux écoles : Minecraft: Education Edition. Cette version donne des outils de contrôle de Minecraft aux professeurs. Une version pédagogique de Minecraft avait déjà été développée par le studio TeacherGaming, MinecraftEDU, et était utilisée dans près de 40 pays différents, et a été rachetée par Microsoft avec l'annonce du jeu. Minecraft: Education Edition est d'abord disponible en accès anticipé — à la manière du jeu original — à partir de juin 2016. Elle sort officiellement en novembre 2016.\nLes professeurs peuvent télécharger ou téléverser des cartes pré-faites sur le site officiel. Le jeu est payable par abonnement annuel.", "chapTitle": "Spin-offs" }, { "number": "6", "text": "Critique\n\nMinecraft est un très bon succès critique et commercial.\nLe jeu est principalement apprécié pour son originalité, la dynamique de son système de jeu, et surtout la liberté créative offerte à ses joueurs. Le mécanisme de jeu développé par Notch, apparemment simpliste, montre le bon équilibre entre ces composantes, afin de créer l'effet d'addiction nécessaire à tout bon jeu. Ceci a fait de Minecraft un succès inattendu de grande envergure.\nAlors que le jeu est en version alpha, Scott Munro du Daily Record le présente comme « quelque chose de spécial », et encourage les joueurs à l'acheter. Le site Rock, Paper, Shotgun recommande également la version alpha du jeu, le décrivant comme « une sorte de Stalker en Lego 8 bit entièrement généré ». L'émission Good Game lui donne deux notes : 7,5 et 9 sur 10, appréciant sa créativité et ses possibilités, tout en critiquant l'absence de tutoriel. La même émission le sélectionne plus tard comme « meilleur jeu téléchargeable de 2010 ».\nPC Gamer notamment retient Minecraft comme le quatrième meilleur jeu pour jouer au travail121.\nGamasutra nomme Minecraft comme étant le 8e meilleur jeu de l'année, ainsi que 8e meilleur jeu indépendant de l'année, tandis que Rock, Paper, Shotgun le nomme jeu de l'année. Mod DB récompense le jeu du titre de « jeu indépendant de l'année 2010 », qui est décerné par le vote des visiteurs, ainsi que deux des cinq récompenses « choix de l'éditeur », pour « Jeu le plus innovant » et « Meilleur jeu solo indépendant ». Parmi les récompenses reçues par Minecraft se trouve aussi le titre de « jeu de l'année » de PC Gamer UK, ainsi que les titres de « meilleur premier jeu », « meilleur jeu téléchargeable » et « jeu le plus innovant » au Game Developers Choice Awards 2011, y gagnant ainsi tous les titres pour lesquels il est nommé. S'y ajoutent également des nominations à l'Independent Games Festival de mars 2011, comme « grand prix Seumas McNally », « excellence technologique » et « excellence de conception », ainsi que le « prix de l'audience » voté par la communauté. Le site Jeuxvideo.com lui attribue un 19/20.\n\nVentes\n\nEn septembre 2010, les recettes du jeu s'élèvent à 250 000 USD par jour. Le même mois, Paypal gèle le compte de Notch à cause d'un « retrait ou dépôt suspect ». Ce jour-là, Notch, de son vrai nom Markus Persson, déclare qu'il avait environ 600 000 € sur son compte. Le 12 janvier 2011, le jeu passe la barre du millionième exemplaire vendu. Le 24 avril 2011, le jeu passait la barre des deux millions d'exemplaires vendus, soit seulement 3 mois et demi après le cap du million d'exemplaires. Le 5 août 2011, le jeu dépasse également la barre des 3 millions d'exemplaires vendus. Le 18 février 2012, le jeu s'est officiellement vendu à plus de 5 millions d'exemplaires, pour un total de 22,4 millions de joueurs (versions gratuite et payante confondues). Le 25 mai 2012, plus de 6 millions de versions officielles ont été vendues. Début novembre 2012, ce sont plus de 9 millions de versions officielles qui ont été vendues sur PC et plus de 4 millions sur Xbox 360, et compte plus de 43 862 100 de comptes au jour du 13 octobre 2012. Le 5 avril 2013, la barre des 10 millions de versions PC vendues est atteinte. Le 25 juin 2014, Minecraft atteint 54 millions d'exemplaires vendus toutes versions confondues, ce qui en fait le deuxième jeu vidéo le plus vendu de l'histoire après Wii Sports. Le 8 juillet 2014, Minecraft dépasse les 16 millions de ventes pour sa version PC et devient le jeu PC le plus vendu au monde, dépassant World of Warcraft. Le 30 juin 2015, Jens Bergensten, développeur en chef du jeu, annonce sur Twitter que le jeu s'est vendu à plus de 20 000 000 d'exemplaires. Début 2016, les ventes atteignent 80 000 000 selon le site officiel. Début juin 2016, l'éditeur annonce que le jeu s'est vendu à plus de 100 millions d'exemplaires, ce qui en fait le jeu vidéo le plus vendu de l'histoire. En décembre 2017, les ventes atteignent 144 millions de copies, toutes versions confondues, alors qu'un nouveau record est réalisé par Microsoft en atteignant 74 millions d’utilisateurs actifs mensuel sur Minecraft. En mai 2019, Microsoft dévoile que le jeu s'est écoulé à plus de 176 millions d'exemplaires toutes plate-formes confondues et que sa version free to play chinoise compte 200 millions d'utilisateurs enregistrés.\n\nRécompenses\n•\tBAFTA Children's Award 2015\no\tGame category\n•\tIndependent Games Festival 2011\no\tSeumas McNally Grand Prize\no\tAudience Award\n•\tIndie of the Year Awards 2010\no\tEditor's Choice\n\tBest MOTY/IOTY Promotions\n\tBest Developers of 2010\n\tMost Innovative\n\tBest Single Player\n•\tPlayer's Choice\no\tIndie of the Year\n\nMinecon\n\nLa Minecon est une convention sur le thème de Minecraft, créée et organisée par Mojang depuis 2010. Elle a rassemblé dans sa dernière édition près de 10 000 personnes, la faisant entrer au Guinness World Records, en tant que « plus grande convention pour un seul jeu vidéo ». Depuis 2011, toutes les personnes ayant acheté des billets et ayant validé leur entrée reçoivent une cape sur leur compte, exclusive et rare, portant un motif changeant chaque année. Il n'y a pas eu de Minecon en 2014.\n", "chapTitle": "Accueil" }, { "number": "7", "text": "Exposition\n\nLe 5 mai 2011, Minecraft a été choisi comme l'un des 80 jeux affichés au Smithsonian American Art Museum, à l'occasion de l'exposition « L'art des jeux vidéo » du 16 mars 2012.\n\nProduits dérivés\n\nAdaptation cinématographique\nFin juin 2016, le site officiel de Minecraft dévoile l'arrivée d'un film en 3D produit par Warner Bros. nommé Minecraft, le film (Minecraft: The Movie) alors prévu pour le 24 mai 2019. Il est également dévoilé que le film sera réalisé par Rob McElhenney. Selon 20 Minutes, il sera produit par Roy Lee, qui a réalisé l'adaptation du jouet de construction Lego, La Grande Aventure Lego. En avril 2019, la date de sortie du film est repoussée au 4 mars 2022. Il est en outre révélé, quelques mois plus tôt, que Peter Sollett assurera la réalisation du film au lieu de McElhenney.\n\nLego\n•\tUn produit Lego Ideas a été créé. Lui et ses suites sont des MicroWorld, il existe quatre biomes différents : Forêt, Village, Nether et Ender.\n•\tUne gamme Minecraft est sortie en 2013, avec plusieurs ensembles comme une cabane, une mine, une maison, etc.\n\nRomans\n\nJournal d'un Noob\n•\tErik Gunnar « Cube Kid » Taylor (trad. Agathe Franck), Guerrier, 404 éd., coll. « Journal d'un Noob » (no 1), 2016, 270 p. \n•\tErik Gunnar « Cube Kid » Taylor (trad. Agathe Franck), Super Guerrier, 404 éd., coll. « Journal d'un Noob » (no 2), 2016, 277 p. \n•\tErik Gunnar « Cube Kid » Taylor (trad. Agathe Franck), Méga Guerrier, 404 éd., coll. « Journal d'un Noob » (no 3), 2016, 270 p. \n•\t\nLa Guerre des blocs\n•\tLiam O'Donnell (trad. Nazim Lebdai), Descente dans l'Overworld, 404 éd., coll. « La Guerre des blocs » (no 1), 2016, 272 p. \n•\tLiam O'Donnell (trad. Nazim Lebdai), Cauchemar dans le Nether, 404 éd., coll. « La Guerre des blocs » (no 2), 2016, 281 p. \n•\tLiam O'Donnell (trad. Nazim Lebdai), Prisonnier de l'Ender, 404 éd., coll. « La Guerre des blocs » (no 3), 2016, 240 p. \n\nEscape Book\n•\tIrving Le Hen, Prisonnier de l'Overworld, 404 éd., coll. « Escape Book » (no 3), 2017, 240 p. \n\nLes Fous du Puy\nLe spectacle du Puy du Fou a donné lieu à une « reconstitution » dans Minecraft.", "chapTitle": "Postérité" }] }],
    [{ "Title": "Free SAS" }, { "Chapitres": [{ "number": "1", "text": "Free propose, en France, le 26 avril 1999 un accès à Internet sans abonnement ni numéro surtaxé. Ses prédécesseurs sur le créneau de l'accès sans abonnement furent World Online (1er avril 1999), puis Freesurf (19 avril 1999). Le nom de Free (« gratuit ou libre » en anglais) vient du fait que l'entreprise propose un accès bas débit à Internet gratuit en modem RTC hors coût des communications téléphoniques, au tarif local et régional.\nCe service inclut le courrier électronique (POP3/IMAP4) accessible aussi par une interface Web IMP ou Zimbra. La destruction des courriers indésirables, ainsi qu'un classement automatique des courriers entrants dans différents répertoires (si on utilise l'interface Web), est possible grâce à l'outil mfilter. Dans le passé, mfilter permettait aussi d'envoyer une réponse automatique personnalisée à l'expéditeur déclaré des mails refusés. Cette possibilité a été supprimée entre le 23 et le 26 août 2007. Cette suppression a pour but d'éviter les problèmes de boucle avec les mails en réponse automatique signalant que l'expéditeur du spam n'existe pas.\nFree est aussi l'une des entreprises françaises pionnières dans l'accès et l'offre Internet par ADSL, après un début chaotique au début de l'année 2000 qui servait principalement de test : à l'époque, les infrastructures techniques et légales n'étaient pas au rendez-vous (conflit notamment avec France Telecom sur le dégroupage).\nFree lance en novembre 2002 une offre ADSL dégroupée à un prix plus attractif que ses concurrents, en ajoutant, plusieurs mois après, des services comme la téléphonie illimitée (août 2003) vers les fixes (VoIP : « Voice over IP » ou « voix sur réseau IP ») ou la télévision (décembre 2003) pour les abonnés bénéficiant de la Freebox et ceci sans surcoût d'abonnement. Le tarif a longtemps été fixé à 29,99 € par mois, jusqu'en février 2011. Les opérateurs concurrents reprochent parfois (par voies indirectes) à Free d'avoir imposé des prix trop bas sur le marché.\nEn 2004, Free est le second fournisseur d'accès en France à proposer le dégroupage total, après Alice ADSL (Telecom Italia à la fin de 2003) et permet au client final de s'affranchir de l'abonnement direct à France Télécom.\nFree propose également un hébergement web gratuit dont la capacité initiale de 100 Mo est passée à 1 Go depuis début 2005 puis 10 Go depuis juin 2006 par défaut avec support de PHP et MySQL. Depuis septembre 2006, Free propose également PostgreSQL. De plus, Free propose maintenant une gestion de plugins permettant de transformer son espace Web en wiki, album photo, blog… (ceci est actuellement suspendu à cause de bugs récurrents).\nEn septembre 2006, Free annonce une offre fibre optique en FTTH (Fiber To The Home) sur sa Freebox HD lancé cette même année. Fin septembre 2012, son président indique que le cuivre utilisé sur les lignes téléphoniques avait « encore beaucoup à donner », faisant allusion au VDSL2 qui permet un débit théorique de 100 Mbit/s et dont l'expérimentation a débuté en France au second semestre 2012 pour un début de mise en place fin 2013.\nLe groupe Iliad a créé la filiale Free mobile en 2009 sur le modèle de Free SAS, dont le même fondateur, Xavier Niel, a présenté lors d'une conférence le 10 janvier 2012, deux offres de téléphonie mobile sans engagement de durée. Ces services ont rapidement gagné des abonnés grâce à des tarifs inférieurs à ceux du marché.\nEn décembre 2010, Free lance sa nouvelle offre Freebox Révolution avec un débit théorique montant maximal en FTTH de 1 Gbit/s.\nLe 19 avril 2012, Free annonce le lancement de FreeWifi Secure à destination des utilisateurs mobile : il s'agit d'une connexion automatique au réseau Free Wi-Fi via la carte SIM des téléphones mobiles, sans avoir besoin de saisir des identifiants. Ce service utilise le protocole EAP-SIM.\nEn 2012, les négociations de la société Free avec Google butent pour financer la connexion entre le service Youtube et les abonnés Free, exposant ces derniers à une dégradation de la qualité de service.\nEn 2013, Free est client de « 100 % des NRA-MED rattachés à un répartiteur dégroupés », même si 5 % ont été ratés. Alice ADSL devient en 2013 la marque low cost de Free. L'offre d'Alice ADSL n'est plus commercialisée depuis 2018.\nEn décembre 2018, Free lance sa nouvelle offre Freebox Delta avec un débit théorique montant maximal en FTTH de 10 Gbit/s EPON (8 Gbit/s maximum) ; cette année Free lance aussi l'offre Delta S qui ne dispose pas de décodeur TV tout en gardant le même débit.\nEn juin 2020, Free propose une carte interactive permettant de tester son éligibilité à la fibre en France. Ce dispositif est disponible pour les abonnés ADSL souhaitant migrer vers la fibre.\n\nInternationalisation\n\nFree possède le 3e réseau privé (DWDM classique) en termes de couverture nationale et de capacité (58 300 km de fibre optique en décembre 2010). Le réseau de Free est désormais international. Il est présent :\n•\taux États-Unis:\no\tà Washington dans le centre Equinix de Ashburn\no\tà New York où il est connecté au point d'échange PAIX-NY depuis Londres\no\tà New York où il est connecté dans le centre Equinix (mais les liaisons ne sont pas utilisées au 5 mai 2010) depuis Paris (Bezon et Telehouse 2)\no\tà Miami où il est connecté au point d'échange Nap Of The America (NOTA) dans le centre Terremark depuis Ashburn\n•\tau Royaume-Uni où il est connecté au point d'échange London Internet Exchange à Londres depuis Paris, Amsterdam et Gravelines\n•\taux Pays-Bas où il est connecté au point d'échange AMS-IX à Amsterdam depuis Londres et Francfort)\n•\ten Allemagne où il est connecté au point d'échange DE-CIX à Francfort depuis Paris, Strasbourg et Amsterdam\nBien que ces liaisons soient souvent sur des fibres optiques louées à d'importants opérateurs internationaux (Level 3 Communications, Global Crossing…), cela permet de diminuer significativement les coûts liés aux accords de transit (payants) en multipliant les accords de peering (gratuits).\nDes accords de transit importants existent encore car Free s'appuie sur deux prestataires (Level 3 et Teleglobe) avec lesquels il dispose d'une capacité de transit de 160 Gbit/s.\nFree opérait aussi un point d'échange à Paris nommé FreeIX où étaient interconnectés jusqu'à 47 fournisseurs d'accès ou de service Internet (tels que Bouygues Telecom, Neuf Cegetel, Global Crossing, Interoute…) ou des fournisseurs de contenu (Google…). Il a été définitivement fermé le 30 août 2011.\nEn Italie : en mai 2018 la direction de Free annonce qu’elle va intégrer le marché italien sous la marque Iliad. Free a décidé de proposer un forfait très avantageux pour les Italiens : 5,99 euros par mois inclut les SMS et la voix en illimité et 30 gigas de data par mois. Ce prix est bien inférieur (entre 50 % et 70 %) à ceux proposés par les principaux concurrents italiens (Wind Tre, Vodafone ou TIM). Le 18 juillet 2018, 50 jours après son lancement, Iliad Italia annonce officiellement avoir déjà atteint son premier million de clients.\nLe 1 octobre 2019, Tigo Sénégal, deuxième opérateur téléphonique du pays, change de marque pour devenir Free Sénégal, après que l'entreprise ait été partiellement acquise par Xavier Niel en 2018. Free (Sénégal) se lance sur le mobile et sur le marché des entreprises.\n", "chapTitle": "Histoire" }, { "number": "2", "text": "La stratégie low cost de Free conduit à proposer des prestations différenciées entre d'une part, le peu de grandes villes qui bénéficient de la fibre optique et d'autre part, l'installation tardive de l'ADSL par des NRA-MeD, financés pour l'essentiel par les impôts. La promotion qu'en fait Free suscite une position sceptique de l'Association des Villes et Collectivités pour les Communications électroniques et l’Audiovisuel (Avicca, organisateur du colloque annuel Territoires et Réseaux d'Initiative Publique).\nIl est arrivé que pour certaines zones non dégroupées, un bridage des protocoles de P2P mis en place se soit transformé en blocage quasi total, contrariant par ce fait toute utilisation légale du P2P.\n", "chapTitle": "Stratégie" }, { "number": "3", "text": "Sur plus de 25 millions d'abonnés, les principaux fournisseurs d'accès à Internet (FAI) en France sont :\n•\tOrange : 40 % de parts de marché, soit 10,354 millions d'abonnés au 31 décembre 2014.\n•\tFree (groupe Iliad) (comprenant aussi Alice ADSL) : 5,868 millions d'abonnés.\n•\tSFR-Numericable : 6,577 millions d'abonnés.\n•\tBouygues Telecom (groupe Bouygues) : 2,428 millions d'abonnés.\n•\tAutres (Alsatis, Nordnet, OVH Télécom, Prixtel, Budget Telecom, Coriolis Télécom, Virgin Mobile, Vivéole, FDN, Nerim, Magic OnLine…) : 6,57 % soit 1,27 million abonnés.\n", "chapTitle": "Part de marché" }, { "number": "4", "text": "La Freebox a été conçue par Sébastien Boutruche, responsable recherche et développement Freebox, avec son équipe, d'après le concept formulé par Xavier Niel et Rani Assaf en 2001.\nFreebox est une première mondiale, elle est la première Box Triple Play, c'est-à-dire le premier terminal réseau offrant accès simultanément à Internet, à la télévision et au téléphone. Depuis, de nombreux fournisseurs d'accès français ont donné à leur modem un nom se terminant par « box », imitant ainsi le nom de la Freebox.\n\nCritiques, plaintes et condamnations\n\nConditions générales de ventes, tarifs et pratiques commerciales\n\nLe 21 février 2006, Free est condamné par le tribunal de grande instance de Paris à payer 30 000 € à titre de dommages-intérêts à l'UFC-Que choisir et 15 000 € à l'association Familles de France pour 32 articles dans les CGV de Free jugés abusifs. Free a réagi en regrettant « l'instrumentalisation des tribunaux par certaines associations de consommateurs en mal de publicité et de financement ». Un peu plus de deux mois plus tard, Free informe ses abonnés du changement des CGV.\nEn mai 2006 l'association de défense des consommateurs UFC-Que choisir lance plusieurs centaines de procédures judiciaires contre la société, à la suite d'un grand nombre de plaintes d'abonnés. À la suite d'une plainte contre X en diffamation, les dirigeants d'UFC-Que choisir ont été mis en examen. Cette dernière plainte a finalement été rejetée par la cour d'appel de Paris (11e chambre). En juillet 2007, Free est condamnée à rembourser trois abonnés et à payer 20 000 € de dommages et intérêts à l'UFC-Que choisir, mais elle fait appel. Un nouveau procès est prévu en novembre 2009.\nEn octobre 2009, l'association UFC-Que choisir attaque Free qui est le seul FAI français à surtaxer sa hotline (0,34 € la minute sans compter les surfacturations des opérateurs de téléphonie mobile). Le 7 décembre 2009, la même association annonce qu'elle a saisi le tribunal de grande instance de Paris à l'encontre de Free pour « pratiques déloyales et illicites dans ses conditions de vente ».\nEn mars 2011, le tribunal de grande instance de Paris, saisi par l'association de défense des consommateurs UFC-Que choisir en mars 2010, a ordonné à Free de cesser ses « pratiques commerciales illicites » et ses « pratiques commerciales trompeuses ».\nLa justice a déclaré illégaux les frais d'activation différés : 96 € (moins 3 € par mois d’ancienneté et moins 1,50 € par mois pour Freebox Révolution) facturé aux abonnés souhaitant résilier leur abonnement. Pour la justice, il s'agissait de frais de résiliation non justifiés : l’article L. 121-84-7 du code de la consommation oblige les opérateurs à facturer des frais de résiliation en adéquation avec leur coût réel. Le tribunal a également donné raison à l'UFC-Que choisir sur d'autres pratiques, mais auxquelles Free a mis fin depuis le dépôt de la plainte de l’UFC Que choisir en mars 2010 : option Service Plus pré-cochée ou encore l'utilisation de termes trompeurs (« Il a Free, il a tout compris » ; « Découvrez les 20 exclusivités de l'offre Free » ; « Bénéficiez de tarifs attractifs vers les autres destinations et mobiles »).\nLe 7 février 2012, Free est condamné par le tribunal correctionnel de Paris pour « pratiques commerciales trompeuses » à l'occasion du lancement de son offre triple-play en 2005. La société doit s'acquitter d'une amende de 100 000 €, payer 40 000 € de dommages et intérêts à l'UFC-Que choisir, modifier ses conditions générales dans un délai de 15 jours sous peine d'une astreinte de 2 000 € par jour de retard et publier la condamnation dans la presse et sur la page d'accueil de leur site interne.\nEn 2012 une affaire de justice remet en question le fait que Free ait été élu en 2010 au prix service client de l'année dans la catégorie Opérateurs de Téléphonie fixe et Internet lors d’une étude réalisée par BVA-Viséo Conseil. SFR a fait condamner Free en 2012 parce que « Free était le seul candidat en lice et obtenu de Free qu’il supprime toute référence à cette \"élection\" ».\nEn 2014, lors de la Coupe du monde de football de 2014, BeIn Sports (France) a demandée à Free d'arrêter la diffusion de chaîne turque TRT 1 (canal 583) sur la Freebox qui diffusait gratuitement et intégralement le mondial, alors que cette dernière ne possédait les droits de retransmission uniquement pour la Turquie.\n\n", "chapTitle": "Freebox" }, { "number": "5", "text": "En 2012, plusieurs témoignages révèlent les pratiques de licenciement chez Free, qui, note L'Expansion, « n'hésiterait pas à utiliser des méthodes humiliantes pour congédier ses salariés ».\nEn 2016, Politis, qui a eu accès à des documents internes à Free, dénonce un fichage, des licenciements abusifs, ou encore une répression syndicale dans l'entreprise. Les journalistes de l'hebdomadaire notent avoir consulté « un tableau extrêmement détaillé, évaluant les pertes aux prud’hommes en comparaison de ce qu’aurait coûté un plan de licenciement conforme à la loi. Le résultat est sans appel : économiquement, mieux vaut harceler, licencier et être condamné que respecter la loi ». Ces méthodes auraient cours depuis 2010. Le journal en ligne Mediapart, en octobre de la même année, pointe également les licenciements abusifs pratiqués par l'entreprise, notamment à l'égard de représentants syndicaux.\nEn 2017, l'émission télévisée Cash Investigation met en cause les conditions de travail chez Free, critiquant les salaires trop faibles, les sanctions prononcées par l'entreprise contre des salariés grévistes voire le chantage à l'emploi. Le rôle d'Angélique Gérard est en particulier pointé du doigt, par exemple via un courriel envoyé au directeur général d'Iliad Maxime Lombardini, où elle écrit : « Nous liquiderons les 50 détracteurs », en parlant de 50 grévistes marocains. Le reportage pointe en effet la répression du droit de grève et du droit syndical exercée par Free au Maroc, où l'entreprise possède deux centres d'appels, soit 1 800 salariés.\n", "chapTitle": "Management et licenciements" }] }],
    [{ "Title": "Akinator : Le Génie du web" }, { "Chapitres": [{ "number": "1", "text": "En 2007, Arnaud Megret, ingénieur en informatique, a l'idée de créer Devinettor, « projet pas très ambitieux, pour s'amuser », où une intelligence artificielle pose 20 questions à un joueur pour deviner à quel personnage il pense. L'ingenieur remplit au départ la base de données avec 100 personnages et 100 questions mais la force du jeu est de s’enrichir au fil des parties : quand le jeu est battu par le joueur car il n'a pas su deviner le personnage, Devinettor enregistre les informations de la partie. Ainsi, la base de données se diversifie au fil des parties et les personnages précédemment joués sont de plus en plus facilement devinés par l'intelligence artificielle.\n\nArnaud Megret partage le jeu à ses connaissances et peu après, Devinettor commence à circuler sur les forums en ligne et atteint une relative popularité rapidement et le jeu est renommé en Akinator.", "chapTitle": "Genèse du projet : Devinettor" }, { "number": "2", "text": "Le jeu devient célèbre en Europe en 2009 et au Japon en 2010 avec le lancement de la version mobile par Scimob ui bat un record sur l'App Store. Akinator est développé par la société Elokence.\n\nEn octobre 2017, l'application Akinator est mis à disposition des utilisateurs sur l'assistant personnel Google Home.", "chapTitle": "Notoriété mondiale" }, { "number": "3", "text": "Le jeu se déroule entre Akinator, représenté sous la forme d'un génie oriental, et un joueur. Akinator essaie de deviner le personnage réel ou fictif (ou l'objet) auquel le joueur pense, et pour ce faire il lui pose des questions. Les questions sont choisies de façon à pouvoir éliminer plusieurs possibilités. Akinator possède une base de données qui regroupe des personnages et des questions avec leurs réponses.\n\nAu bout d'une vingtaine de questions, Akinator a généralement pu éliminer toutes les possibilités sauf une, et propose au joueur sa réponse. S'il se trompe, il propose alors au joueur de continuer en lui posant une nouvelle série de questions (qui ont souvent pour but d'élargir un peu la recherche, en supposant que le joueur ait pu se tromper quelquefois dans ses réponses). Si après ces questions supplémentaires Akinator se trompe, ou ne trouve pas le personnage, il demande au joueur de lui indiquer quel était le personnage, et ce qui le caractérise.\n\nAkinator repose sur un moteur d'inférence nommé Limule qui est écrit en C++ et le site fonctionne en PHP. Sa base de données contient environ 100 000 personnages. Si, à la fin de partie, Akinator ne trouve pas le personnage, le joueur a la possibilité d'entrer le nom et la photo de son personnage, ce qui enrichit la base de données.\n\nLe jeu Akinator est disponible sur différents supports, sur l'ordinateur via Internet en 14 langues, mais existe aussi comme application pour smartphone depuis 2009. Une version Akinator kid pour les enfants est sortie.", "chapTitle": "Fonctionnement" }, { "number": "4", "text": "L'Express a donné une note de 5 sur 5 à l'application iPhone pour la semaine du 9 septembre 2009. Excite France déclare « plus qu’interactif, il est révolutionnaire ».", "chapTitle": "Réception critique" }] }],
    [{ "Title": "iMac" }, { "Chapitres": [{ "number": "1", "text": "Le nom « iMac » comprend « Mac », contraction de l'appellation Macintosh utilisée pour les ordinateurs de la marque depuis 1984, précédé de la lettre (minuscule) « i ». Dans la présentation du premier iMac par Steve Jobs le 6 mai 1998, le « i » évoque à la fois « internet », « individual », « instruct », « inform » et « inspire ».", "chapTitle": "Dénomination" }, { "number": "2", "text": "Sculpté par Jonathan Ive, l'iMac première version (1998) a créé un véritable choc dans l'industrie informatique, en mettant en avant l'utilisation du design dans ce secteur. Tout d'abord, l'esthétisme joue à la fois sur la forme ronde, les couleurs chaudes, les plastiques doux et dont la couleur ne passe pas avec le temps. Clavier et souris sont assortis à la couleur de l'iMac ; le clavier est réduit à 88 touches, afin de ne pas être plus large que l'écran.\n\nDesign au sens plein du terme, l'esthétisme accompagnant des fonctions ergonomiques : format monobloc facilitant la prise en main, poignée pour le transport, ouïes pour l'aération, emplacements du processeur, de l'alimentation et de la carte graphique (principales sources de chaleur) choisis sur la carte mère pour permettre une évacuation optimale (à la verticale) de la chaleur, et donc la suppression, dans les trois dernières versions, des ventilateurs, sources de bruit. Les branchements sont tous regroupés à portée de la main, sur le côté droit de l'ordinateur (quand on le regarde), donc accessibles pour un droitier : ports USB, Ethernet, modem, entrée et sortie son, puis ajout de ports FireWire ; en façade, deux prises casques supplémentaires permettent un usage à deux d'un logiciel éducatif, par exemple, ce qui ajoute encore une note de convivialité à l'usage de cet ordinateur. L'iMac première version est un des premiers ordinateurs à avoir intégré le port USB qui, à l'époque, venait d'être spécifié, et qui deviendra par la suite très populaire. L'iMac marque aussi une rupture technologique en n'intégrant pas délibérément de lecteur de disquette, ce qui amorcera le déclin de ce format de stockage.", "chapTitle": "Design" }, { "number": "3", "text": "L'iMac G3\n\nL'iMac G3 est le premier modèle d'iMac. Il combine, dans un seul et même boîtier, un écran de 15 pouces et une unité centrale. Initialement uniquement disponible en bleu bondi, il est plus tard disponible en d'autres couleurs. L'iMac G3 est livré avec un clavier et une souris (branchée sur le clavier), s'accordant avec la couleur du boîtier.\n\nLancé le 6 mai 1998 à Cupertino comme le premier Macintosh, il est commercialisé entre août 1998 et mars 2003, avant d'être remplacé par l'iMac G4.\n\niMac G4 (tournesol)\n\nEn 2002, Apple sort l'iMac G4, équipé d'un processeur PowerPC G4, avec un design encore plus évolué et une puissance toujours plus grande, le tout étant concentré dans une base hémisphérique d'un diamètre de 27 cm, surmontée d'un écran plat orientable grâce à un bras articulé, ce qui lui a valu son surnom de tournesol. Le modèle haut de gamme possède un graveur de DVD. Doté à l'origine d'un écran 4:3 de 15 pouces, la gamme s'est étoffée progressivement avec la sortie de modèles à écran 16:10 de 17, puis 20 pouces.\n\niMac G5\n\nLe 31 août 2004, à l'Apple Expo de Paris, Phil Schiller — vice-président d'Apple — annonce l'iMac de troisième génération. Celui-ci est doté d'un design encore plus compact : l'ordinateur tient dans ce qui semble n'être que l'écran, plat de surcroit. Cela est rendu possible par l'usage de composants d'ordinateurs portable, ce qui rappelle le Twentieth Anniversary Macintosh ; l'ensemble ne fait pas plus de 5 cm d'épaisseur, et est porté par un pied unique, permettant l'ajustement vertical de l'écran. Le slogan « Où est passé l'ordinateur ? » jouait sur la confusion que pouvait ressentir une personne croyant n'avoir que l'écran en face d'elle et cherchant l'unité centrale. L'iMac G5 est doté d'un processeur PowerPC G5, d'un écran plat 16:10 de 17 ou 20 pouces et d'un graveur de CD-ROM ou de DVD-ROM à fente (« mange-disque ») positionné sur sa tranche.\n\niMac Intel\n\nLancés en janvier 2006, les iMac Intel sont les premiers Macintosh utilisant un processeur Intel. Ils marquent avec le MacBook Pro le coup d'envoi de la transition progressive du PowerPC vers Intel effectuée par Apple. Utilisant d'abord des processeurs Intel Core Duo, une mise à jour fin 2006 remplace ces Core Duo par son successeur l'Intel Core 2 Duo qui ouvre la voie aux iMac Dual Core (2 cœurs) puis Quad Core (4 cœurs) avec l'apparition des premiers Core iX. Son aspect est directement hérité de l'iMac G5, avec un boîtier en polycarbonate blanc, et un écran de format 16:10.\n\niMac aluminium\n\nAu mois d'août 2007, Apple met à jour l'aspect de ses iMac. Le boîtier est désormais en aluminium avec une dalle en verre pour protéger l'écran (toujours 16:10). Ils sont toujours équipés de processeurs Intel, mais ceux-ci sont plus véloces rendant la machine plus puissante.\n\nDeux autres mises à jour suivent en avril 2008 et mars 2009, celles-ci modifiant principalement les caractéristiques techniques de la machine (on peut les reconnaître au pied plus fin sur le bord avant), et introduisant notamment les premiers processeurs Quad-Core (iMac i5 27 pouces).\n\niMac 16:9 (fin 2009)\n\nEn octobre 2009 est présenté un tout nouvel iMac, avec un écran au format 16:9 et une vision à 178° (IPS). Le style aluminium et verre de la génération précédente est conservé, tout en étant remis au goût du jour. Les formats sont aussi modifiés, passant du 20 et 24 pouces au 21,5 et 27 pouces.\n\niMac 2012\n\nFin novembre 2012, Apple présente un nouveau modèle d'iMac, presque identique au précédent, avec des bords plus fins4. Cet iMac est le premier dans lequel il est possible de rajouter un Fusion Drive ou un SSD de grande capacité. Autre nouveauté : le SuperDrive et le port FireWire ne sont plus présents dans ce Mac.\n\niMac 2013\n\nDébut octobre 2013, Apple présente de nouveaux modèles pour sa gamme d'iMac. Sans changement majeur, c'est surtout une mise à jour des composants internes qui est effectuée.\n•\tPassage aux processeurs Intel Haswell.\n•\tPour les modèles 21,5 pouces, les cartes graphiques nVidia sont remplacées par les puces Intel Iris Pro.\n•\tLes modèles 27 pouces gardent leurs cartes nVidia, mais optent pour de plus récentes.\n•\tSupport de la norme Wi-Fi 802.11ac.\n•\tPossibilité d'avoir 1 To de disque dur SSD.\n•\tDiminution des prix par rapport à la gamme 2012.\n\niMac Retina 5K\n\nAu mois d'octobre 2014, Apple présente un nouveau modèle d'iMac équipé d'un écran Retina 5K d'une résolution de 5 120 × 2 880 pixels, supérieure au standard 4K (3 840 × 2 160).\n\nC'est un modèle à part de la gamme iMac, qui, d'ailleurs, n'a pas été renouvelée lors de cette présentation.\n\nSeulement disponible en format 27 pouces, ce modèle intègre de meilleurs processeurs Intel Core i5 (ou i7 en option) ainsi qu'une carte graphique AMD, à la différence des autres iMac qui possèdent des puces nVidia. C'est également le seul modèle qui intègre le système Fusion Drive en standard, avec une capacité de 1 To.\n\nEn octobre 2015, le Magic Keyboard 2 et la Magic Mouse 2 sont inclus dans l'iMac 5K.\n\niMac Retina 4K\n\nAu mois d'octobre 2015, Apple sort un modèle 4K pour l'iMac 21,5 pouces. L'iMac 4K est fourni avec de nouveaux périphériques de saisie, la Magic Mouse 2 et le Magic Keyboard. La Magic Mouse 2 intègre désormais une batterie que l'on peut recharger via son port Lightning.\n\nCe modèle intègre des processeurs Intel Core i5 (ou i7 en option).\n\n", "chapTitle": "Modèles" }] }],
    [{ "Title": "Informaticien" }, { "Chapitres": [{ "number": "1", "text": "Le métier d'informaticien apparaît à la fin du XIXe siècle avec l’émergence de la mécanographie. Celle-ci consiste alors à traiter l'information à l'aide de systèmes électromécaniques. Les mécanographes sont ainsi employés à s'occuper de ces systèmes et ce n'est qu'au milieu du XXe siècle, avec l'arrivée du terme allemand « Informatik » créé par l'ingénieur Karl Steinbuch et repris en France par Philippe Dreyfus, que le terme d'informaticien voit le jour.\nDans les années 1960 à 1980, on nomme ainsi informaticien toute personne exerçant un métier en rapport avec l'informatique. Le métier est alors peu valorisé, il est largement exercé par les femmes ingénieures. Depuis le milieu des années 1980 et l'arrivée de micro-ordinateurs dans les foyers, ce domaine est largement investi par les hommes, dans les pays occidentaux. Dans les écoles d'informatique, les femmes forment 15 % des effectifs en 2018.\nLa variété et le peu de rapport des métiers du génie informatique fait tomber le terme informaticien en désuétude en France au profit de noms plus spécifiques : agent d'exploitation, administrateur système, responsable de sécurité, administrateur de sécurité, administrateur réseau, analyste, programmeur, architecte informatique, etc. Ces métiers peuvent concerner le domaine matériel et/ou le domaine logiciel. « Informaticien » est donc un terme générique désignant des métiers très éloignés les uns des autres et propre à une époque où on ne les distinguait pas toujours.\nDepuis le début du XXIe siècle, le terme est différencié de l’opérateur, qui se sert de l'informatique comme d'un outil destiné à son propre métier. La profession d’informaticien regroupe ainsi tous les corps de métier qui visent à concevoir, à coordonner ou à mettre en œuvre le développement ou le déploiement d'une solution informatisée qui est mise à la disposition des opérateurs appelés alors utilisateurs.\n", "chapTitle": "Historique" }, { "number": "2", "text": "Le travail d'un informaticien logiciel (programmeur) est d'utiliser ses connaissances en langages informatiques (Assembleur, Fortran, Pascal, Cobol, RPG, Python, C, C++, Java, C#, Visual Basic, Visual Basic for Applications,html , MAD...) afin de concevoir et de superviser le développement d'applications informatiques ou de logiciels.\nCe travail se traduit concrètement par différentes activités, souvent liées à l'âge et à l'expérience de l'informaticien logiciel :\n•\tle débutant, sorti de l'école ou de la faculté, fera généralement du développement ; il code les tâches décrites d'un programme selon les spécifications qui lui sont fournies. Il effectue aussi de la maintenance sur des programmes existants, ainsi que des évolutions (ajouts de fonctionnalités aux programmes, etc.) ;\n•\taprès quelques années, l'informaticien logiciel dispose d'une meilleure connaissance technique et métier. En commençant à encadrer de plus jeunes développeurs, l'une des évolutions possibles est de devenir chef de projet. La connaissance grandissante des techniques et du métier permet à l'informaticien logiciel de conseiller les utilisateurs ou clients afin de les aider à cerner leurs besoins, évoquer des fonctionnalités oubliées, etc. Il propose également des solutions techniques sur lesquelles il tranche avec le client : interface locale sur chacun des postes, interface web partagée, base de données… Il peut ensuite devenir architecte du système d'information ou responsable du système d'information, à la tête de plusieurs projets d'envergure, comme un progiciel de gestion intégré.\nFormations les plus courantes :\n•\técole d'ingénieurs en informatique ;\n•\tdiplôme universitaire en informatique ;\n•\tdiplôme universitaire en génie logiciel ;\n•\tbrevet de technicien supérieur en informatique.\nL'évolution du métier d'informaticien logiciel est soumise à certaines turbulences depuis la fin du XXe siècle. Les fonctions d'un programmeur débutant ne comportent pas toujours de responsabilités de conception et impliquent rarement un rôle de direction de projet. Mais en même temps, les règles d'avancement, les grilles de rémunération en vigueur et les critères de reconnaissance sociale limitent considérablement les possibilités de carrière pour les programmeurs, provoquant un phénomène de « fuite des cerveaux » vers d'autres métiers. Les programmeurs de haut niveau sont donc extrêmement rares; on les trouve principalement dans les cellules de recherche et développement des constructeurs informatiques et éditeurs de logiciels (certains ont d'ailleurs une notoriété internationale). Il est à noter aussi que, dans les autres entreprises, la dévalorisation de la fonction se manifeste notamment par la quasi-disparition du mot programmeur dans les intitulés de fonction des informaticiens qui exercent ce métier (nommés de préférence ingénieurs d'étude, ingénieur de développement, etc.).\n", "chapTitle": "L'informaticien logiciel et programmation" }, { "number": "3", "text": "•\tles administrateurs de bases de données ou DBA : chargés du bon fonctionnement d'une base de données et/ou d'un système de gestion de base de données ;\n•\tles administrateurs réseau : chargés de gérer les comptes et les machines d'un réseau ;\n•\tles administrateurs système : chargés de la maintenance applicative des serveurs ;\n•\tles analystes-programmeurs : chargés de spécifier techniquement les concepts définis par le Concepteur (ou analyste) en composants informatiques ;\n•\tles architectes de systèmes d’information : chargés de définir la cartographie de systèmes informatiques (logicielle et matérielle) ;\n•\tles chercheurs : chargés de formaliser les problèmes à résoudre, de développer des algorithmes permettant de les résoudre, de définir de nouvelles structures de données, de nouveaux concepts, de nouveaux langages de programmation ou de nouveaux systèmes informatiques ;\n•\tles développeurs : chargés de la programmation au sein du projet ;\n•\tles pen-testeurs : chargés, avec l'accord de leurs propriétaires, d'attaquer les systèmes informatiques en vue d'évaluer l'efficacité des solutions de sécurité mises en place et d'en proposer des améliorations (voir test d'intrusion) ;\n•\tles techniciens de maintenance : chargés de l'assistance technique, de la disponibilité des postes de travail, des sauvegardes de données, du déploiement des ordinateurs, etc. Ils doivent veiller au bon fonctionnement du parc informatique et faire de la maintenance \"préventive\" ;\n•\tles techniciens en télécommunications ;\n•\tles chefs de projets : chargés de la rédaction des cahiers de charges des applications manuelles ou innovantes et à élaborer les résultats informatiques exigés par les demandeurs. Une autre de leurs tâches consiste en la planification des projets, techniquement et en termes de ressources humaines ;\n•\tles concepteurs (ou analystes) : chargés d'identifier les besoins des utilisateurs et de les spécifier. Leur rôle consiste en particulier à expliquer les concepts à des experts non informaticiens ;\n•\tles consultants : chargés par essence d'analyser un environnement, un besoin ou un problème informatiques sur les plans fonctionnel et technique et de proposer un ou plusieurs scénarios d'évolution ou de résolution adéquats. Leur champ d'activité est, en pratique, beaucoup plus variable et vaste : il s'étend du conseil à la gestion de projet, en passant par l'action commerciale ;\n•\tles directeurs des systèmes d'information chargés d'encadrer l'ensemble de l'activité informatique d'une structure ;\n•\tles ergonomes informatiques : chargés notamment d'améliorer la convivialité et l'efficacité des interfaces homme-machine ;\n•\tles rédacteurs techniques : chargés de produire la documentation destinée aux métiers ;\n•\tles responsables de la sécurité des systèmes d'information : chargés de la sécurité des systèmes d'information ;\n•\tles techniciens helpdesk : chargés d'assister les usagers par téléphone ;\n•\tles testeurs ou qualifieurs : chargés de tester le logiciel ou les chaines de programmes produit par les programmeurs, par exemple durant la période de VABF ;\n•\tles urbanistes : chargés de redéfinir les projets sur le plan fonctionnel ;\n•\tles Community manager ou les Gestionnaires de communautés, qui animent et fédèrent des communautés sur Internet pour le compte d'une société, d'une marque, d’une célébrité ou d’une institution.\n•\tles webmasters : chargés de la ligne éditoriale et du contenu des sites Internet ;", "chapTitle": "Métiers de l'informatique" }] }],
    [{ "Title": "Zoubida" }, { "Chapitres": [{ "number": "1", "text": "Zoubida est un prénom et surnom féminin arabe souvent donné au nord du Maroc :\n•\tZoubida Assoul (1956-), femme politique algérienne ;\n•\tZoubida Bouazoug (1976-), une judoka algérienne ;\n•\tZoubida Bouyacoub (1987-), une judokate algérienne ;\n•\tZoubida Laayouni (1956-), une athlète marocaine spécialiste du lancer du disque.\n", "chapTitle": "Prénom et surnom" }, { "number": "2", "text": "Zoubida possède de nombreuses variantes orthographiques :\n•\tZoubaida :\no\tIrene Zoubaida Khan (1956-), personnalité féminine bangladaise, la septième secrétaire générale d'Amnesty International.\n•\tZubayda :\no\tZubayda bint Jafar ou Zobéide (morte en 832), la petite fille du calife abbasside Al-Mansour. Elle et son mari Hârûn ar-Rachîd sont des personnages du mythe des Mille et une nuits.\n•\tZoubeida :\no\tZoubeida Bchir (1938-2011), une poétesse et productrice de radio tunisienne ;\no\tPrix Zoubeida Bchir, un prix littéraire donné chaque année par le Centre de recherches, d'études, de documentation et d'information sur la femme de Tunisie ;\n•\tZoubeïda :\no\tZoubeïda Bittari, pseudonyme de Louise Ali-Rachedi (1939-), une écrivaine algérienne\n•\tZubaida :\no\tZubaida Tharwat (1940-2016), une actrice égyptienne ;\n•\tZubeida :\no\tZubeida ou Zubaida ou Zubeida Begum Dhanrajgir (1911-1988), une actrice indienne ;\no\tZubeida Agha (1922-1997), une peintre pakistanaise ;\no\tZubeida Malik (en), une journaliste britannique ;\no\tZubeida Mustafa (en) (1941-), une journaliste pakistanaise ;\no\tZubeida Rahimtoola (en) ou Zubeida Sultan Chinoy (1917-), une femme politique pakistanaise ;\n•\tZubeidaa :\no\tZubeidaa, un film indien réalisé par Shyam Benegal sorti en 2001 où l'actrice principale porte le prénom Zubeidaa.\n", "chapTitle": "Variantes" }, { "number": "3", "text": "•\tZoubida, une chanson française écrite par François Valéry et S. Lebel, interprétée par Charlotte Julian et commercialisée à partir de 1978 par le label Sonopresse. Elle présente une certaine joie de vivre à l'orientale.\n•\tZoubida, une chanson franco-algérienne de 1982, interprétée par Rachid Taha évoquant la question des mariages forcés.\n•\tLa Zoubida, une chanson française interprétée par Vincent Lagaf' sur l'air de la chanson traditionnelle Sur l'pont du Nord (ou de Nantes, selon les traditions) ;\no\tLa Zoubida, un sketch des Les Inconnus où le (faux) groupe Ze Cioure (une parodie de The Cure) chantait dans un clip qui passait au Top 50 la reprise de ce titre ;\no\tLagaf': Les Aventures de Moktar - Vol 1: La Zoubida, un jeu vidéo inspiré par la chanson Zoubida de Vincent Lagaf', édité par Titus Interactive, sorti en 1991.\n", "chapTitle": "Titres" }] }],
    [{ "Title": "Mario" }, { "Chapitres": [{ "number": "1", "text": "Jumpman\n\nMr Video est le premier nom donné par Shigeru Miyamoto à Mario. Selon le jeune développeur, ce personnage destiné à apparaître dans tous les jeux qu'il réaliserait se devait de posséder une stature propre, contrairement aux autres jeux de cette époque (Pac-Man, Space Invaders). Mais ce nom est rapidement abandonné pour Jumpman en 1981, dans le jeu vidéo Donkey Kong.\nCe nom reflète simplement le principe du jeu, où le but est de gravir des échafaudages pour délivrer Pauline, l'amie du héros, qui se trouve au sommet, captive du gorille Donkey Kong. Pour cela, Jumpman doit éviter tonneaux et boules de feu en sautant (jump). Le jeu se déroule sur un chantier, et le personnage contrôlé par le joueur a un métier : il est charpentier.\n\nMario\n\nLe personnage de Jumpman est rebaptisé Mario dans Donkey Kong Jr. sorti en arcade en 1982. C'est la seule fois où il endosse le rôle d'antagoniste du personnage contrôlé par le joueur, en l'occurrence le fils de Donkey Kong, dont le but est de délivrer son père captif de Mario.\nLe retour au premier plan de Mario se fait en 1983 dans Mario Bros., épisode qui voit également l'apparition de son frère Luigi. Dans ce jeu, les deux frères ne peuvent pas sauter sur leurs ennemis, mais doivent les faire trébucher pour ensuite les éjecter hors de l'écran. Le succès est important, mais Miyamoto considère que son personnage n'est qu'un élément se déplaçant, l'important étant de créer un gameplay autour. C'est deux ans plus tard que Mario connaîtra véritablement la notoriété, sur la première console de Nintendo, la Famicom (et sa version occidentale, la NES).\n", "chapTitle": "Origines du personnage" }, { "number": "2", "text": "Nom\n\nLes sources divergent quant à l'origine du patronyme. Le nom de Mario aurait été choisi en l'honneur du propriétaire des locaux de la société Nintendo of America de l'époque, Mario Segali5. Mais, selon Eiji Aonuma, proche collaborateur de Shigeru Miyamoto, Mario serait l'abréviation de marionnette, choisi par Miyamoto à cause de son amour des automates et du bunraku, le théâtre de marionnettes japonais. Shigeru Miyamoto, quant à lui, a déclaré lors d'une interview que c'est un employé de Nintendo of America qui lui avait suggéré le nom de Mario. Lors des vidéos précédant la sortie de Super Mario Maker, Miyamoto confirme que le nom vient bel et bien de Mario Segali.\nDans le film Super Mario Bros., le prénom et le nom de famille du personnage sont tous deux Mario ; cependant, Shigeru Miyamoto a précisé, lors d'une interview, que Mario n'avait pas de nom de famille.\n\nMissions\n\nMario s'est fait une spécialité de sauver les princesses en détresse et notamment la Princesse Peach (souvent enlevée par Bowser ou Bowser Junior), dont il semble amoureux, ce qui est peut-être réciproque7. Bien que cette mission soit présente dans de nombreux jeux, il arrive parfois à Mario de sauver Daisy ou encore Pauline, notamment dans Donkey Kong et dans la série Mario vs. Donkey Kong qui a 6 volets. De plus, il n'est pas rare qu'il tente de libérer le Royaume Champignon voire d'autres royaumes comme le Royaume de Végésia.\nIl porte parfois secours à son univers dans Super Mario Galaxy et Super Mario Galaxy 2 ou même à l'ensemble de toutes les dimensions dans Super Paper Mario. Il doit aussi dans Super Mario Bros. 2 détruire la machine diabolique du maléfique Wart. Il peut également être l'objet de la quête au lieu d'en être l'acteur (Mario is Missing sur Super NES, Super princesse Peach sur DS) ou bien devoir affronter son rival Wario, apparu premièrement dans Super Mario Land 2: 6 Golden Coins sur Game Boy au début des années 1990.\n\nApparence\n\nSelon son créateur (Shigeru Miyamoto), l'apparence de Mario ne doit rien au hasard. Miyamoto a avoué que sa casquette lui venait du fait qu'il trouvait compliqué de dessiner des cheveux et que si Mario passait sur un tableau dont le fond est noir (les jeux étaient en 2D) on ne les verrait plus. Ainsi a-t-il pensé à une casquette rouge. Aussi, la moustache est apparue à cause des limitations techniques de l'époque en évitant d'avoir à dessiner la bouche (élément trop fin). Pour la salopette, il était essentiel de la dessiner avec deux couleurs différentes pour ne pas confondre les bras avec le corps. Mais il faudra attendre 1988 dans la version américaine et européenne pour que le choix des couleurs soit fixé : haut rouge et salopette bleue et non l'inverse comme dans Donkey Kong (en 1981) ou la salopette marron dans Super Mario Bros. (en 1985). Comme Miyamoto voulait baser le design de Mario sur quelqu'un de « normal » (contrairement à beaucoup de héros ou de super-héros, qui sont très souvent musclés et grands), il lui a dessiné un gros nez, un ventre rond et une taille moyenne.\n\nDescription et pouvoirs\n\n \nMalgré son apparence banale de plombier, Mario possède une très grande force et des capacités de sauts incroyables. Sa rapidité lui confère une grande habileté au combat. Malgré son physique un peu disgracieux (petit, un peu corpulent avec un gros nez), il est courageux et peut venir à bout de n'importe quelle situation.\nMême s'il parle anglais (« Let's Go! », « It's me, Mario! »…), Mario a un prénom et un accent italiens. Quand il rêve dans Super Mario 64 et Mario vs. Donkey Kong, on l'entend marmonner dans son sommeil « spaghetti », « ravioli ». De plus, une des phrases qui lui sont souvent attachées est \"Mamma Mia !\", par exemple lorsqu'il se fait toucher par une carapace ou une peau de banane (dans Mario Kart), ou, plus généralement, lorsqu'il perd une vie ou fait Game Over, ou encore quand il perd une vie dans certaines versions de Super Mario Bros.\nEn plus d'avoir été charpentier puis plombier, Mario s'est essayé à différentes activités : médecin, conducteur de kart, tennisman, joueur de baseball mais aussi golfeur, footballeur, basketteur, danseur, athlète, joueur de balle au prisonnier, de hockey, de volleyball et peintre.\nMario dispose aussi d'une très grande panoplie de transformations, les principales lui étant données par un champignon qui le rend grand, lui permettant ainsi de briser des blocs de briques et obligeant l'ennemi à le toucher deux fois au lieu d'une pour le tuer, puis une fleur de feu lui permettant de lancer des boules de feu, et l'étoile qui lui confère une invincibilité provisoire (il garde sa taille et ses pouvoirs).\nSelon les jeux, ce genre d'options et la façon dont elles s'échangent varie. Dans Super Mario Bros. 3, Mario perd le pouvoir de la fleur s'il se fait toucher et redevient grand alors que dans la plupart des jeux ultérieurs il redevient petit avec un système de récupération du pouvoir d'être grand (champignon en réserve). Dans New Super Mario Bros. sur Nintendo DS ou autre, Mario peut avoir une carapace bleue, un champignon géant qui le fait devenir gigantesque ou le Mini-champi qui le rend minuscule. Dans New Super Mario Bros. Wii mais pas seulement, il dispose de la fleur de glace lui conférant le pouvoir de lancer des boules de glace, du champignon hélico qui lui permet de voler pendant une courte durée et d'un costume de pingouin permettant à Mario de devenir un pingouin pour glisser sur la glace et lancer des boules de glace.\n\nPersonnages récurrents\n\nMario a un petit frère, Luigi, qui lui ressemble physiquement. Ce dernier est cependant plus grand, plus maigre, un peu moins courageux, maladroit mais il est plus léger et saute plus haut dans certains jeux. Il porte des vêtements verts et bleu marine. Des jeux Nintendo lui sont dédiés comme Luigi's Mansion sur GameCube, Luigi's Mansion 2 sur Nintendo 3DS et Luigi's Mansion 3 sur Nintendo Switch.\nOn voit presque toujours Toad, un champignon anthropomorphique. Les Toads sont les serviteurs de la Princesse Peach. C'est en tout cas un allié indéfectible de Mario.\nMario possède aussi une monture, Yoshi, un animal à forme de dinosaure en différentes couleurs, notamment en vert, qui apparaît pour la première fois dans Super Mario World. Yoshi est le héros de plusieurs jeux vidéo.\nWario est le rival de Mario. Il porte des vêtements jaune et violets, et est le héros de deux séries à part entières : Wario Land et Wario Ware.\nLuigi a également son double maléfique, Waluigi, qui porte des vêtements de couleur violette et qui joue aussi un rôle important, notamment dans Mario Power Tennis sur GameCube.\nOn peut noter que Wario et Waluigi sont des sortes de caricatures de Mario et Luigi. En effet, Mario étant plus petit et plus rond que son frère, Wario est petit et gros, tandis que Waluigi est très grand et filiforme.\nLa fin de Super Mario World semble indiquer que Mario est amoureux de la princesse Peach mais le jeu indique aussi un intérêt pour la princesse Daisy. Peach a une robe rose et des cheveux blonds alors que Daisy a une robe orange et des cheveux bruns courts depuis un moment. Sur la Nintendo 64 elle a des cheveux long châtain et une robe jaune, preuve s'il en fallait que le character design évolue.\nLe pire ennemi de Mario reste Bowser, le roi des Koopas, dont il fait échouer les plans diaboliques depuis la naissance.\nLes Koopas sont des tortues récurrentes dans l'univers de Mario. Leurs carapaces sont de différentes couleurs (verte, rouge, bleue et jaune), qui symbolisent généralement chacune des caractéristiques différentes. Il existe également des Koopas squelettes (appelés Skélérex), etc.\n\nBébé Mario\n\nDans certaines aventures qui se déroulent dans le passé, comme Mario et Luigi : Les Frères du temps ou Yoshi's Island, Mario apparaît sous la forme de Bébé Mario. Toutefois, sa première apparition date de Super Mario World 2: Yoshi's Island. Il reviendra ensuite dans plusieurs jeux, en tant que simple caméo dans Super Smash Bros. Melee, Super Smash Bros. Brawl et Mario Kart DS, ou en tant que personnage jouable dans Mario Golf, Mario Tennis, Mario Tennis Open, Mario Superstar Baseball, Mario Super Sluggers, Mario Kart : Double Dash !!, Mario Kart Wii ou encore Mario Kart 8.\n\nEnnemis\n\n \nBowser, le roi des Koopas, et ses 7 généraux (Iggy, Larry, Lemmy, Ludwig, Morton, Roy et Wendy) ainsi que son fils Bowser Jr. sont les plus grands ennemis de Mario. Wario peut être considéré plus comme un rival que comme un véritable ennemi du héros. Les ennemis de base sont généralement les Koopas, les Goombas, les Bob-omb, les Boos, les Maskass et les Bill Balls. Les ennemis plus forts sont Flora Piranha, Donkey Kong, le Roi Boo, Wiggler, Graburge, Kamek, etc. Mais il existe aussi des ennemis emblématiques mais peu courants comme Wart (Super Mario Bros. 2) ou Birdo…\n\nMétier de Mario\n\nDans la culture populaire, Mario est connu pour être plombier. Cependant, Miyamoto aurait d'abord choisi de faire de Mario un charpentier dans Donkey Kong. C'est dans le jeu Mario Bros. sorti sur borne d'arcade en 1983 qu'il devint un plombier, combattant les créatures des égouts de New York avec son frère Luigi. Mario occupa aussi plusieurs autres métiers à travers les jeux dont médecin, golfeur, artiste et même sportif olympique.\nEn 2017, Nintendo annonce que Mario n'est plus plombier12, avant de remettre cette profession en activité principale en 2018.", "chapTitle": "Personnage" }, { "number": "3", "text": "Mario apparaît pour la première fois dans Donkey Kong en 1981, mais le premier jeu qui porte son nom est Mario Bros. en 1983. Peu de temps après, il connaît un véritable succès dans une série de jeux de plates-formes, les Super Mario.\nCette série de Super Mario a joué un rôle important dans l'évolution du jeu de plates-formes, en renouvelant régulièrement les codes (blocs-bonus, pièces à collectionner, sauts sur la tête des ennemis pour s'en débarrasser, etc.), offrant ainsi à chaque épisode un gameplay de qualité. De multiples épisodes ont marqué durablement le jeu vidéo, notamment le premier opus, Super Mario Bros., énorme succès sorti en 1985 qui demeurera, jusqu'à la fin de l'année 2008, le jeu le plus vendu de tous les temps avec plus de 40 millions de cartouches écoulées (mais détrôné depuis par Wii Sports sur Wii et Mario Kart Wii). Cet épisode est considéré comme l'un des plus novateurs de l'histoire du jeu vidéo, et a posé les bases du jeu de plate-forme ensuite reprises dans d'innombrables jeux. Il est régulièrement cité comme l'un des meilleurs jeux de tous les temps par les sites Internet et magazines qui font ce genre de classement. La musique, œuvre de Koji Kondo, imprime le rythme du jeu, avec des thèmes célèbres devenus indissociables du personnage.\nLe virage 3D a été pris avec Super Mario 64, considéré par certains comme le meilleur jeu de plates-formes existant. Il s'agit d'un des premiers jeux à profiter pleinement du joystick analogique, l'une des grandes innovations de la Nintendo 64. Là encore, sa structure est devenue une nouvelle référence, désormais pour le domaine de la plate-forme 3D, et fut reprise par de nombreux autres jeux.\nAu fil des années, Mario est également apparu dans les Mario Kart (des jeux de course), les Mario Golf ou Mario Tennis (des jeux de sport), les Mario Party (des mini-jeux) et les jeux Super Smash Bros. (des jeux de combat), qui furent toujours de grands succès commerciaux.\nDans Mario et Sonic aux Jeux olympiques, il se retrouve pour la première fois aux côtés de Sonic, la mascotte de Sega qui fut souvent considérée comme le principal concurrent de Mario en raison de la rivalité entre Sega et Nintendo qui avait lieu dans les années 1990. Il fait équipe avec lui dans Mario et Sonic aux Jeux olympiques d'hiver, Mario et Sonic aux Jeux olympiques de Londres 2012 et Mario et Sonic aux Jeux olympiques d'hiver de Sotchi 2014 pour déjouer les plans de Bowser et du Dr Eggman.\nLe 16 novembre 2007, le jeu de plates-formes Super Mario Galaxy est sorti en France sur Wii. Il innove en proposant désormais au joueur d'évoluer dans un univers où Mario se déplace de (petites) planètes en planètes, intégrant ainsi une nouvelle dynamique de plate-forme où l'attraction terrestre devient un point primordial. Là encore, de nombreux sites Internet et magazines lui accordent le statut de meilleur jeu de l'année, et il fut même pendant quelque temps numéro 1 du classement des jeux recevant les meilleurs critiques de tous les temps selon le site GameRankings. En 2010, sa suite Super Mario Galaxy 2 sort en mai-juin dans laquelle Mario est accompagné de son fidèle compagnon Yoshi.\nEn 2006, Nintendo décide de revenir aux sources de la série en sortant sur DS le premier Super Mario en 2D depuis Super Mario Land 2, sorti en 1992, baptisé New Super Mario Bros.. Il s'agit d'un succès colossal, sur le point de devenir l'épisode le plus vendu depuis le premier Super Mario Bros. et l'un des jeux les plus vendus de tous les temps. Fort de ce succès, un nouvel épisode sort sur Wii en 2009, New Super Mario Bros. Wii qui se distingue notamment par l'apport d'un mode multijoueur jouable jusqu'à 4. Le célèbre magazine japonais Famitsu lui décerne la note parfaite de 40/40, ce qui, en plus de 23 ans d'histoire, n'a été attribué qu'à 15 jeux.", "chapTitle": "Historique des jeux avec Mario" }] }],
    [{ "Title": "Luigi" }, { "Chapitres": [{ "number": "1", "text": "De clone de Mario à personnage à part entière\n\nIl apparaît pour la première fois sous la forme d'un palette swap dans Mario Bros. sur Game and Watch en mars 1983.\nLe choix de Mario comme nom du personnage principal ne découle visiblement pas d'un emprunt à l'Italie. Selon un collaborateur de Shigeru Miyamoto, Mario serait une réduction de « Marionnette », le genre d'abréviation de mot d'origine occidentale typique au Japon. Toutefois, l'homonymie a inévitablement entraîné un rattachement à des origines italiennes pour le personnage lorsque le jeu a débarqué dans nos contrées. Selon Shigeru Miyamoto, il fallait donc que le frère de Mario suive le mouvement, et sa connaissance de nombreux designers italiens avec ce prénom l'ont fait penché pour le prénom de Luigi. Au-delà de la simple évocation d'un prénom italien, Luigi pourrait lui aussi être rattaché au Japon ; écrit ruiji [類似(るいじ)] en japonais, il peut signifier, en fonction des idéogrammes attribués, « le type similaire » ou « le clone » (de « rui » signifiant « ressemblance » et « ji » abréviation d'« o-jisan», qui signifie « le type ».\nIl est également présent dans Wrecking Crew.\nLongtemps visuellement identique à Mario, l'identité de Luigi s'affirme dans Super Mario Bros. 2 japonais (connu en Europe et aux États-Unis sous le nom de Lost Levels), et surtout dans Super Mario Bros. 2 dit \"USA\" (le volet sorti en occident à la place de Lost Levels). Luigi y apparaît comme un héros fantasque, gauche mais aux capacités de saut supérieures à celles de Mario. Surtout, et pour la première fois dans la série, l'apparence de Luigi y est différente de celle de son frère Mario. Il est plus grand que son frère mais moins courageux.\n\nLa concurrence d'autres personnages de Nintendo\n\nLuigi connaîtra un passage à vide durant les années 1990. Il redevient « pixellement » identique à Mario dans Super Mario Bros. 3, et est relégué au second plan à la suite de l'émergence de nouveaux héros Nintendo, notamment Donkey Kong, Wario et Yoshi. Devenu personnage très secondaire, il sera ainsi absent du jeu Super Mario RPG (mais présent dans le générique de fin et en cameo), et sera écarté de Super Mario 64. À ce sujet, Luigi est au centre de rumeurs affirmant qu'il est possible de l'apercevoir dans le jeu, ce qui n'a jamais été confirmé. D'ailleurs, Nintendo a dit clairement que toutes les astuces pour débloquer Luigi dans ce jeu sont fausses et qu'il n'est pas présent dans le jeu (à moins que vous n'utilisiez un code de triche pour que Mario change la couleur de son costume). Nintendo réparera ensuite cette injustice en intégrant Luigi comme personnage jouable dans le remake sur Nintendo DS de Super Mario 64 (Super Mario 64 DS).\n\nLe retour au premier plan\n\nNintendo envisagea longtemps de ne cantonner Luigi qu'à des rôles de personnages annexes dans les jeux multijoueur comme Mario Kart, Mario Party ou encore Mario Tennis. C'était sans compter le soutien de ses fans inconditionnels, que Nintendo va finir par entendre.\nNintendo fera ainsi de Luigi la mascotte du lancement de la GameCube, avec la sortie du jeu Luigi's Mansion.\nLuigi est également un personnage jouable du jeu de plates-formes Super Mario Galaxy, après avoir obtenu les 120 étoiles avec Mario.\nIl apparaît en tant que personnage principal dans cinq jeux : Luigi's Mansion, sorti en 2002 en Europe, Mario Is Missing! sorti des années plus tôt, \"New Super Luigi U\" sorti en 2012-2013, Luigi's Mansion 2 sorti en 2013 sur Nintendo 3DS et Luigi's Mansion 3 qui est sorti en 2019.\nLa dernière apparition de Luigi en tant que personnage de premier plan est dans New Super Luigi U, un contenu additionnel disponible sur support physique et en téléchargement sur Wii U qui reprend le même univers que New Super Mario Bros. U mais avec un gameplay quelque peu différent. Les niveaux sont modifiés et certains d'entre eux sont limités dans le temps à 100 secondes. Comme à son habitude, Luigi peut sauter plus haut et plus loin, il est plus rapide que Mario mais a besoin d'une distance supérieure pour s'arrêter.", "chapTitle": "Histoire" }, { "number": "2", "text": "Aspect physique\n\nDans Super Mario Bros., sa chemise est verte, tandis que sa casquette et sa salopette sont blanche (cette combinaison de couleurs sera utilisée par la suite pour son costume de Fleur de feu). Luigi est alors contrôlé par le deuxième joueur, devant attendre que le premier joueur (contrôlant Mario) meure pour commencer la partie. La couleur verte a été choisie pour le différencier de son frère et à cause de la mémoire limitée de la console. De ce fait la palette de couleurs pouvant apparaître à l'écran était restreinte. Le vert de Luigi est celui de la carapace des tortues dans le jeu.\n\nCapacités\n\nDans une part significative des jeux de la série Mario, Luigi est identique en tous points à son frère Mario. C'est le cas dans Mario Bros., Super Mario Bros., Super Mario Bros. 3, Super Mario World ou encore New Super Mario Bros..\nCependant, chaque fois qu'il a été possible de distinguer Luigi de Mario dans les jeux de plateformes, le plombier vert dispose de capacités de saut nettement supérieures à celles de son frère, mais se révèle plus difficile à contrôler (distance d'arrêt plus longue). C'est le cas dans Super Mario Bros.: The Lost Levels, Super Mario Bros. 2, Super Paper Mario, Super Mario Galaxy, Super Mario Galaxy 2, Super Mario 3D Land ou Super Mario 3D World.\nDans les jeux multijoueurs, Luigi diffère assez souvent de Mario, bien que ses capacités demeurent généralement proches de celles de son frère. Dans Mario Kart DS, les karts de Luigi peuvent être plus portés vers la vitesse que vers la maniabilité. Luigi est également plus rapide que Mario dans Mario et Sonic aux Jeux olympiques. Il se trouve cependant de nombreux où les capacités de Luigi sont identiques à celles de Mario (Super Mario Kart, Mario Kart: Double Dash!! ou Mario Kart: Super Circuit). À l'inverse, il peut se révéler sensiblement différent de Mario à de rares occasions, comme dans la série Super Smash Bros., où dispose d'une palette de mouvements et de coups spéciaux propres. Luigi apparaît effectivement comme personnage caché dans Super Smash Bros., où ses capacités de saut dépassent celles de Mario, mais ses coups restent majoritairement basés sur ceux de Mario. Dans Super Smash Bros. Melee, il acquiert cependant de nouveaux coups, aussi les différences avec son frère sont désormais plus sensibles. Cependant, il sait faire à peu près les mêmes attaques que Mario. Les \"boules de feu\" de Luigi sont horizontales et vont moins loin que celles de Mario. Le \"Super poing sauté\" de Luigi inflige plus de dommage que celui de Mario seulement s'il est proche de l'ennemi. Il est le personnage du jeu qui \"glisse le plus\" au sol, contrairement à Mario qui est dans la moyenne dans ce jeu. Cela lui a permis d'être prisé par les joueurs avancés pour son \"wave-dash\", une technique avancée permettant de faire glisser rapidement un personnage au sol. Luigi apparaît aussi dans Super Smash Bros. Brawl. Son \"Super poing sauté\" donne moins de pièces que celui de Mario lorsqu'il est effectué dans les airs ou légèrement mais inflige plus de dommages lorsqu'il est effectué au sol. Dans le mode Emissaire Subspatial de Super Smash Bros. Brawl, Luigi tient un rôle mineur. Rapidement capturé par le Roi DaDiDou, il intervient vers la fin de l'histoire une fois libéré.\nEn fonction des jeux et donc des opportunités, Luigi est soit identique à Mario pour ne pas pénaliser ou avantager un joueur (comme dans New Super Mario Bros. Wii), soit différent, afin de proposer une alternative au gameplay traditionnel de Mario (comme dans Super Mario Galaxy pour les jeux à 1 joueur, ou Super Smash Bros. Brawl pour les jeux à possibilité multijoueur).\n\nLa voix de Luigi\n\nC'est l'acteur américain Charles Martinet, déjà doubleur de Mario, qui donne une de ses nombreuses voix à Luigi. Le juste ton de la voix de Luigi a cependant mis un peu de temps à s'affirmer. Dans la version japonaise de Mario Kart 64, (Où Julien Bardakoff prête sa voix à Luigi à la place de Charles Martinet.) elle est plus aigüe que celle de Mario, alors que dans les versions occidentales, elle est plus grave. La voix aigüe sera progressivement abandonnée au profit de la voix grave, qui souligne plus le caractère maladroit et mal assuré de Luigi. Cependant, dans Super Smash Bros. et Super Smash Bros. Melee Luigi a récupéré une voix aigüe faite avec des enregistrements de la voix de Mario dans Super Mario 64, mais cela s'explique certainement parce que le plombier vert y est un personnage caché (et donc moins \"travaillé\" que son frère Mario) alors que la version anglaise de Mario Kart 64 et Luigi's Mansion bénéficient de la voix grave que nous connaissons. Cette \"anomalie\" est surtout dû au fait que la plupart des voix du jeu sont directement reprises de précédents opus. Mais dans Super Smash Bros. Brawl, Charles Martinet refit la voix de Luigi pour la faire ressembler à celle de tous les autres jeux. La voix aiguë de Luigi revient une dernière fois dans Mario Kart Super Circuit avant d'être définitivement abandonnée.\n\nBébé Luigi\n\nIl s'agit de Luigi en bébé. Il est pratiquement toujours avec son grand frère Bébé Mario (comme dans Mario Kart: Double Dash !!), qui est peut-être là pour rassurer Bébé Luigi quand ce dernier a trop peur ou quand il pleure. Bébé Luigi se fait surtout remarquer par ses pleurs, comme dans Mario et Luigi : les Frères du Temps, où il pleure à plusieurs reprises. Cependant, il s'agit d'un bébé très mignon, gentil, rusé, joueur. Une citation de sa part dans Mario Kart: Double Dash !! : « Uh, Oh, Baby Weegie Time ! ».\nIl fait sa première apparition dans Super Mario World 2: Yoshi's Island. Joueur de tennis dans Mario Tennis, conducteur ou copilote de kart dans Mario Kart: Double Dash!! ou héros dans Mario et Luigi : Les Frères du temps, il possède en lui de nombreux talents, comme celui du maniement du marteau. Il donne d'ailleurs le sien à Luigi et Bébé Mario donne le sien à Mario. On peut également le voir un court instant dans Yoshi Touch and Go accroché à une cigogne. Il apparait aussi dans Yoshi's Island DS puis dans Mario Kart Wii en tant que personnage à débloquer en débloquant un contre-la-montre «difficile» établi par les concepteurs sur 8 circuits.\n\nRôle dans la série\n\nUne rumeur ferait mention d'une histoire d'amour entre lui et la princesse Daisy, mais Nintendo reste flou. La biographie de la princesse Daisy dans Super Smash Bros. Melee évoque la rumeur, tout en restant évasive. Cette rumeur semble s'être confirmée dans Mario Kart Wii où l'on trouve sur le Circuit Daisy une statue de Luigi et Daisy en train de danser ensemble... Notez qu'une statue avec Bébé Luigi et Bébé Daisy évoque la même chose.\nDans le jeu RPG Mario et Luigi Superstar Saga, il ne semble pas insensible aux charmes de Prince Harrick, qui lui porte un certain intérêt.\nEn dehors de ces quelques rumeurs, Luigi n'a pas d'intérêt amoureux confirmé.\nDepuis Mario Tennis, Luigi est également doté d'un rival, Waluigi, personnage très secondaire de l'univers Nintendo, qui symbolise le côté obscur du héros à la casquette verte, tout comme Wario qui s'oppose à Mario.\nC'est un croupier hors pair dans Super Mario 64 DS et New Super Mario Bros.. On peut donc penser qu'il est un adepte du poker et d'autres jeux de hasard: il est donc possible qu'il travaille dans un casino.", "chapTitle": "Caractéristiques" }, { "number": "3", "text": "Série Mario\n\nSon « premier rôle » remonte au jeu Mario a disparu ! sorti en 1993 sur Super Nintendo. Toutefois c'était plus un jeu éducatif (géographique notamment) qu'autre chose. Il aura ensuite fallu attendre 2001 pour que Luigi soit la vedette de son propre jeu, Luigi's Mansion, sur la console GameCube, suivi d'un second opus en 2013 sur Nintendo 3DS. Également, il a été cohéros dans la série Mario and Luigi, commencée en 2003 sur Game Boy Advance.\nDans New Super Mario Bros. et New Super Mario Bros. 2, un code (L+R+A au moment de choisir son fichier) permet de jouer avec Luigi en mode 1 joueur à la place de Mario. Il peut être choisi par les deuxième, troisième ou quatrième joueur dans New Super Mario Bros. Wii, et en mode 1 joueur, il est possible de faire appel à lui grâce à l'option \"super guide\". Un Luigi contrôlé par l'ordinateur prend alors la place de Mario, et montre ainsi au joueur comment traverser les pièges.\nIl est également possible de le contrôler dans Super Mario Galaxy, après avoir récolté toutes les étoiles du jeu. Pour cela, il faut sélectionner Luigi quand on charge la partie. Luigi est indispensable au jeu quand on prend Mario puisqu'il lui permet de récolter un certain nombre de super étoiles. Au lieu de mettre Mario à la place de Luigi pour aider ce dernier devenu personnage principal, on trouve en fait un deuxième Luigi, qui s'étonnera d'ailleurs de se retrouver face à lui-même.\nIl est également jouable dans Super Mario Galaxy 2. À la différence du premier volet, le joueur peut être amené à le contrôler pour quelques niveaux ponctuels, remplaçant ainsi Mario. De plus, une fois remporté l'affrontement final contre Bowser, il est ensuite possible de l'incarner à tout moment à la place de Mario.\n\nAutres jeux\n\nIl apparaît dans les différents épisodes de la série Super Smash Bros. en tant que personnage à débloquer.", "chapTitle": "Apparitions" }] }],
    [{ "Title": "Lomepal" }, { "Chapitres": [{ "number": "1", "text": "Jeunesse et formation\n\nAntoine Valentinelli est né le 4 décembre 1991 dans le 14e arrondissement de Paris. Il est le fils de Dominique Valentinelli, lecteur-correcteur chez Gallimard, et d'une mère artiste-peintre. Il évolue ainsi dans un environnement aussi artistique qu'instable. En effet, les revenus de sa mère, qui l'élève, sont très irréguliers : « Classe moyenne, un peu bohème. J'ai vécu dans un immense atelier où il y avait de grandes expos. Ma mère vendait bien et, à d'autres moments, c'était beaucoup plus difficile financièrement. » Son père quitte la maison alors qu'il est encore jeune, Antoine ne grandira donc pas avec lui mais entouré de ses trois sœurs et de sa mère. L'époque du lycée illustre bien cette situation, le jeune Antoine se fait renvoyer du lycée Rodin à Paris 13e et entraîne même la mutation de la proviseure, il déclarera à propos de cette période « Chez moi, c'était la guerre : mon père se faisait la malle, ma mère était à l'hôpital, je n'avais pas la tête à l'école. » Il entame par la suite des études de montage, qu'il arrête pour se consacrer au rap.\n\nDébuts (2011–2016)\n\nProche du collectif L'Entourage, il est devenu rappeur par le biais du skate, sa première passion à cause de laquelle il s'est « déjà tout cassé 5 fois », dit-il. « C'est bien simple : je ne me rappelle pas ne pas avoir skaté un jour pendant dix ans. » En 2011, il sort son premier titre À la trappe sous le pseudonyme de Jo Pump, en featuring avec Nekfeu. Il continue en solo avec son premier extended play (EP) intitulé 20 mesures. En 2013, un second, Le singe fume sa cigarette, avec le compositeur Hologram Lo et le rappeur belge Caballero ainsi qu'un autre EP baptisé Cette foutue perle. En 2014, il poursuit avec l'EP Seigneur. L'année suivante, il sort Majesté. En 2016, il publie ODSL, un EP en collaboration avec S-Two.\n\nFlip (2017)\n\nLe 30 juin 2017, Lomepal publie son premier album studio, FLIP. La pochette de l'album montre l'artiste travesti en femme sur fond rose, ce qui constitue un fait très atypique dans le milieu hip-hop, d'influences relativement masculines. Le titre de son album solo fait référence à une figure classique dans le monde du skate, le kick-flip (figure qui consiste en un saut suivi d'une rotation) : « Avant, je ne voulais pas forcément en parler dans ma musique, car je voulais le faire bien. J'ai attendu ce moment et c'est là, maintenant. » Jouant également sur les multiples sens que flip peut avoir, il apparaît en femme sur la pochette. L'album est promu grâce au single Yeux disent qui rencontre un succès rapide. L'accueil critique est favorable et en février 2018, FLIP est certifié double disque de platine.\n\nJeannine (2018)\n\nLe 7 décembre 2018, Lomepal dévoile son second album intitulé Jeannine, où l'on retrouve des collaborations avec Roméo Elvis, JeanJass, Orelsan et Philippe Katerine. L'album est annoncé fin septembre 2018 avec le single 1000°C, en duo avec Roméo Elvis. La pochette, plus sobre, présente ici la silhouette de Lomepal sur un fond bleu ciel. Le nom de cet album fait référence à sa grand-mère, qui était atteinte de schizophrénie.\nLe 19 octobre 2019, l'artiste sort en collaboration avec Arte un film mi-documentaire mi-session live intitulé 3 Jours à Motorbass où il interprète des morceaux issus principalement de ses deux premiers albums en version acoustique. Un album rassemblant l'ensemble des morceaux sort peu après.\nLe 25 octobre 2019, Lomepal sort Amina, réédition de Jeannine accompagnée de onze titres supplémentaires, dont cinq morceaux inédits, mais aussi trois versions acoustiques, un instrumental et une maquette.\nEn 2020, il est nominé aux Victoires de la musique dans la catégorie « artiste masculin » face à Philippe Katerine et Alain Souchon.\n\nAccident (2018)\n\nLors de vacances à l'île Maurice en 2018, accompagné de plusieurs amis dont le rappeur belge Caballero, il est victime d'un grave accident de voiture. En sortant d'un restaurant dans lequel ils fêtaient l’anniversaire de Caballero, ils décident de prendre leur voiture malgré leur alcoolémie. Leur voiture heurte un trottoir puis un poteau. L'accident n'a fait aucun blessé, cependant Lomepal en reste traumatisé. Il se confie sur cet accident dans la chanson Flash sortit dans la réédition de son album Jeannine, intitulé Amina sortit le 25 octobre 2019, \"Trois bouteilles vides, on roulait vite/On aurait dit la fin d'un film [...] La Cooper s'est crashée, j'entends encore le son/Bruyant comme la mort dans une belle chanson\". À l'occasion de l’anniversaire de son ami Caballero, le 22 novembre 2019, il publie des photos et vidéos datant du jour de l'accident sur son compte Instagram, il accompagne sa publication d'un message dans lequel il déclare : « C’est quand on a tapé une première fois le trottoir en rigolant que j’ai réalisé que cette soirée ressemblait beaucoup trop a un spot de prévention routière. Seul dieu sait pourquoi la décapotable n’a pas fait de tonneaux et pourquoi la route était vide à ce moment là mais on a tous survécu.. On est sortis de ce cercueil de fer et j’ai pleuré comme un bébé. [...] on est pas morts sur cette route et maintenant je sais quel drôle de bruit ça fait une voiture qui heurte un poteau à 100 km/h, j’ai jamais eu autant envie de vivre que depuis ce jour là. »\n", "chapTitle": "Biographie" }, { "number": "2", "text": "Lomepal tire son inspiration du thème de la solitude en général du rap politique. Au début, il rappait autour du voyage, de la sensation de tout vouloir quitter, des thèmes qui sont aussi assez liés à la solitude selon lui.\n\nIl a l'habitude de composer avec une instrumentale en fond sans la voix du rappeur pour ne pas être déconcentré, ce qui lui donne des idées sur des flows, des manières d'attaquer la rime. Il écrit sans cesse sur son téléphone portable, dès qu'il a une idée qui lui passe par la tête, le plus souvent c'est dans les transports qu'il s'y met. Cela lui permet d'écrire beaucoup. À ce sujet, il déclare : « La plupart de mes textes sont des pièces recousues que j’assemble par-ci par-là après les avoir grattées à la volée. »", "chapTitle": "Styles et influences" }, { "number": "3", "text": "•\t2011 : 20 mesures\n•\t2013 : Cette foutue perle\n•\t2014 : Seigneur\n•\t2015 : Majesté\n•\t2017 : FLIP\n•\t2018 : Jeannine\n•\t2019 : Amina (réédition de Jeannine)\n•\t2019 : 3 Jours à Motorbass", "chapTitle": "Discographie" }] }]
];

